/**
Any error thrown by the compiler due to a problem with the input source code.
*/
export default class CompileError extends Error {
	constructor(warning) {
		super(warning.message)
		/** Location and description of the error. */
		this.warning = warning
	}
}

/**
Any problem with source code.
Despite the name, this is used for both warnings and errors.
*/
export class Warning {
	constructor(loc, message) {
		/**
		Source location of the problem.
		@type {Loc}
		*/
		this.loc = loc
		/**
		Text description of the problem.
		@type {string}
		*/
		this.message = message
	}

	/**
	Applies `codeFormatter` to parts of `this.message` created by {@link code}.
	@param {function(code: string)} codeFormatter
	@return
		Generator yielding strings (for non-`code`)
		and results of `formatter(code)` for `code` parts.
	*/
	* messageParts(codeFormatter) {
		const message = this.message
		const codeRegex = /{{(.*?)}}/g
		let prevIdx = 0
		for (;;) {
			const match = codeRegex.exec(message)
			if (match === null) {
				yield message.slice(prevIdx, message.length)
				break
			} else {
				yield message.slice(prevIdx, match.index)
				yield codeFormatter(match[1])
				prevIdx = codeRegex.lastIndex
			}
		}
	}
}

/** Used when generating warning messages to highlight a part of that message. */
export const code = str =>
	`{{${str}}}`

import CompileError from './CompileError'
import CompileOptions from './private/CompileOptions'
import {setContext, unsetContext, warnings} from './private/context'
import lex from './private/lex/lex'
import parse from './private/parse/parse'
import render from './private/render'
import transpile from './private/transpile/transpile'
import verify from './private/verify/verify'

export default class Compiler {
	/**
	@param {object} options
	@param {'\t'|Number} [options.indent='\t']
		Mason does not allow mixed kinds of indentation,
		so indent type must be set once here and used consistently.
		If '\t', use tabs to indent.
		If a Number, indent with that many spaces. Should be an int 2 through 8.
	@param {string} [options.mslPath='msl']
		Path to `msl`. This may be `msl/dist`.
	@param {boolean} [options.checks=true]
		If false, leave out type checks and assertions.
	@param {boolean} [options.includeSourceMap=true] See @return for description.
	@param {boolen} [options.importBoot=true]
		Most mason modules include `msl/private/boot`, which `msl`.
		If you don't want to do this, much of the language will not work.
		This is only intended for compiling `msl` itself.
	*/
	constructor(options = {}) {
		this.options = new CompileOptions(options)
	}

	/**
	@param {string} source Mason source code for a single module.
	@param {string} filename Path of the source file.
	@return {{warnings: Array<Warning>, result: CompileError | {code: string, sourceMap: string}}}
		`CompileError`s are not thrown, but returned.
		This allows us to return `warnings` as well.
		`sourceMap` will be empty unless `opts.includeSourceMap`.
	*/
	compile(source, filename) {
		return this._doInContext(filename, () => {
			const ast = parse(lex(source))
			return render(transpile(ast, verify(ast)))
		})
	}

	/**
	Return a {@link MsAst} rather than transpiling it to JavaScript.
	Parameters are the same as `compile`.
	@return {{warnings: Array<Warning>, result: CompileError|MsAst}}
	*/
	parse(source, filename) {
		return this._doInContext(filename, () => {
			const ast = parse(lex(source))
			verify(ast)
			return ast
		})
	}

	/**
	Get the CompileError class associated with this compiler.
	This is used by mason-node-util to avoid depending on mason-compile directly.
	*/
	get CompileError() {
		return CompileError
	}

	_doInContext(filename, getResult) {
		setContext(this.options, filename)
		try {
			const result = getResult()
			return {warnings, result}
		} catch (error) {
			if (!(error instanceof CompileError))
				throw error
			return {warnings, result: error}
		} finally {
			unsetContext()
		}
	}
}

import {Keywords, keywordName, reservedKeywords as reserved} from './private/Token'

/**
Array of the names of every keyword, not including reserved words.
Alphabetically sorted.
*/
export const keywords =
	Object.keys(Keywords).map(key => keywordName(Keywords[key])).sort()

/**
Array of the names of every reserved word.
Alphabetically sorted.
*/
export const reservedKeywords = reserved.sort()

import defaultBuiltins from './defaultBuiltins'
import {cat, last} from './util'

/**
Stores `opts` parameter to compile methods and supplies defaults.
See {@link compile} for description of options.
*/
export default class CompileOptions {
	constructor(opts) {
		const define = (name, _default) => {
			this[`_${name}`] = opts[name] === undefined ? _default : opts[name]
		}

		const defaults = {
			includeAmdefine: false,
			includeSourceMap: true,
			lazyModules: false,
			useStrict: true,
			checks: true,
			importBoot: true,
			mslPath: 'msl',
			indent: '\t'
		}

		const allOpts = new Set(cat(Object.keys(defaults), 'builtins'))

		for (const _ in defaults)
			define(_, defaults[_])

		for (const _ in opts)
			if (!allOpts.has(_))
				throw new Error(`Unrecognized option ${_}`)

		const minIndent = 2, maxIndent = 8
		if (!(this._indent === '\t' || minIndent <= this._indent && this._indent <= maxIndent))
			throw new Error(`opts.indent must be '\t' or a number 2-8, got: ${this._indent}`)

		const builtins = opts.builtins || getDefaultBuiltins(this._mslPath)
		this.builtinNameToPath = generateBuiltinsMap(builtins)
	}

	indent() {
		return this._indent
	}

	includeChecks() { return this._checks }

	includeAmdefine() { return this._includeAmdefine }
	includeSourceMap() { return this._includeSourceMap }
	includeUseStrict() { return this._useStrict }

	lazyModule() { return this._lazyModules }

	importBoot() { return this._importBoot }
	bootPath() { return `${this._mslPath}/private/boot` }
}

function getDefaultBuiltins(mslPath) {
	const builtins = Object.assign({}, defaultBuiltins)
	if (mslPath !== 'msl')
		for (const key in builtins) {
			const x = builtins[key]
			delete builtins[key]
			builtins[key.replace(/msl/g, mslPath)] = x
		}
	return builtins
}

function generateBuiltinsMap(builtins) {
	const m = new Map()
	for (const path in builtins) {
		const realPath = path.replace(/\./g, '/')
		for (let imported of builtins[path]) {
			if (imported === '_')
				imported = last(path.split('.'))
			if (m.has(imported))
				throw new Error(`Builtin ${imported} defined more than once.`)
			m.set(imported, realPath)
		}
	}
	return m
}

/* eslint-disable indent */

import {cat, opIf} from './util'

/**
Any Mason AST.
All ASTs have a `loc` that they pass on to the esast during {@link transpile}.
*/
export default class MsAst {
	constructor(loc) {
		/** @type {Loc} */
		this.loc = loc
	}
}

// LineContent
	/**
	Any valid part of a Block.
	Note that some {@link Val}s will still cause warnings if they appear as a line.
	*/
	export class LineContent extends MsAst {}

	/** Can only appear as lines in a Block. */
	export class Do extends LineContent {}

	/** Can appear in any expression. */
	export class Val extends LineContent {}

// Module
	/** Whole source file. */
	export class Module extends MsAst {
		constructor(loc, name, opComment, doImports, imports, lines) {
			super(loc)
			/**
			Not used for compilation, but useful for tools.
			@type {string}
			*/
			this.name = name
			/** @type {?string} */
			this.opComment = opComment
			/** @type {Array<ImportDo>} */
			this.doImports = doImports
			/** @type {Array<Import>} */
			this.imports = imports
			/** @type {Array<Do>} */
			this.lines = lines
		}
	}

	/** Single import in an `import!` block. */
	export class ImportDo extends MsAst {
		constructor(loc, path) {
			super(loc)
			/** @type {string} */
			this.path = path
		}
	}

	/**
	Single import in an `import` block.
	If path is 'global', this is transpiled specially because there's no actual 'global' module.
	*/
	export class Import extends MsAst {
		constructor(loc, path, imported, opImportDefault) {
			super(loc)
			/** @type {string} */
			this.path = path
			/** @type {Array<LocalDeclare>} */
			this.imported = imported
			/** @type {?LocalDeclare} */
			this.opImportDefault = opImportDefault
		}
	}

// Locals
	/**
	All {@link LocalAccess}es must have some LocalDeclare to access.
	All accessible identifiers are therefore LocalDeclares.
	This includes imports, `this`, the focus, etc.
	*/
	export class LocalDeclare extends MsAst {
		/** LocalDeclare with no type. */
		static untyped(loc, name, kind) {
			return new LocalDeclare(loc, name, null, kind)
		}

		/** LocalDeclare of just a name. */
		static plain(loc, name) {
			return new LocalDeclare(loc, name, null, LocalDeclares.Eager)
		}

		static built(loc) {
			return this.plain(loc, 'built')
		}
		static focus(loc) {
			return this.plain(loc, '_')
		}
		static typedFocus(loc, type) {
			return new LocalDeclare(loc, '_', type, LocalDeclares.Eager)
		}
		static this(loc) {
			return this.plain(loc, 'this')
		}

		constructor(loc, name, opType, kind) {
			super(loc)
			/** @type {string} */
			this.name = name
			/** @type {?Val} */
			this.opType = opType
			/** @type {LocalDeclares} */
			this.kind = kind
		}

		isLazy() {
			return this.kind === LocalDeclares.Lazy
		}
	}
	/**
	Kind of {@link LocalDeclare}.
	@enum {number}
	*/
	export const LocalDeclares = {
		/** Declared normally. */
		Eager: 0,
		/** Declared with `~a`. */
		Lazy: 1
	}

	/** Access the local `name`. */
	export class LocalAccess extends Val {
		static focus(loc) {
			return new LocalAccess(loc, '_')
		}

		static this(loc) {
			return new LocalAccess(loc, 'this')
		}

		constructor(loc, name) {
			super(loc)
			/** @type {string} */
			this.name = name
		}
	}

	/** `{name} := {value}` */
	export class LocalMutate extends Do {
		constructor(loc, name, value) {
			super(loc)
			/** @type {string} */
			this.name = name
			/** @type {Val} */
			this.value = value
		}
	}

// Assign
	/** Any expression creating new locals. */
	export class Assign extends Do {
		/**
		All locals created by the assign.
		@abstract
		*/
		allAssignees() {}
	}

	/** `{assignee} =/:=/::= {value}` */
	export class AssignSingle extends Assign {
		/** Assign to `_`. */
		static focus(loc, value) {
			return new AssignSingle(loc, LocalDeclare.focus(loc), value)
		}

		constructor(loc, assignee, value) {
			super(loc)
			/** @type {LocalDeclare} */
			this.assignee = assignee
			/** @type {Val} */
			this.value = value
		}

		/** @override */
		allAssignees() { return [this.assignee] }
	}

	/** `{assignees} =/:=/::= {value}` */
	export class AssignDestructure extends Assign {
		constructor(loc, assignees, value) {
			super(loc)
			/** @type {Array<LocalDeclare>} */
			this.assignees = assignees
			/** @type {Val} */
			this.value = value
		}

		/**
		Kind of locals this assigns to.
		@return {LocalDeclares}
		*/
		kind() {
			return this.assignees[0].kind
		}

		/** @override */
		allAssignees() {
			return this.assignees
		}
	}

	/** Kinds of {@link MemberSet} and {@link SetSub}. */
	export const Setters = {
		Init: 0,
		Mutate: 1
	}

	/**
	`{object}.{name}:{opType} =/:=/::= {value}`
	Also handles `{object}."{name}"`.
	*/
	export class MemberSet extends Do {
		constructor(loc, object, name, opType, kind, value) {
			super(loc)
			/** @type {Val} */
			this.object = object
			/** @type {string | Val} */
			this.name = name
			/** @type {?Val} */
			this.opType = opType
			/** @type {Setters} */
			this.kind = kind
			/** @type {Val} */
			this.value = value
		}
	}

	/** `{object}[{subbeds}]:{opType} =/:=/::= {value}` */
	export class SetSub extends Do {
		constructor(loc, object, subbeds, opType, kind, value) {
			super(loc)
			/** @type {Val} */
			this.object = object
			/** @type {Array<Val>} */
			this.subbeds = subbeds
			/** @type {?Val} */
			this.opType = opType
			/** @type {Setters} */
			this.kind = kind
			/** @type {Val} */
			this.value = value
		}
	}

// Errors
	/** `throw! {opThrown}` */
	export class Throw extends Do {
		constructor(loc, opThrown) {
			super(loc)
			/** @type {?Val} */
			this.opThrown = opThrown
		}
	}

	/** `assert!/forbid! {condition} throw! {opThrown}` */
	export class Assert extends Do {
		constructor(loc, negate, condition, opThrown) {
			super(loc)
			/**
			If true, this is a `forbid!`.
			@type {boolean}
			*/
			this.negate = negate
			/**
			Compiled specially if a {@link Call}.
			@type {Val}
			*/
			this.condition = condition
			/** @type {?Val} */
			this.opThrown = opThrown
		}
	}

	/**
	```except
		try
			{try}
		catch
			{opCatch}
		else
			{opElse}
		finally
			{opFinally}```
	*/
	export class Except extends LineContent {
		constructor(loc, _try, typedCatches, opCatchAll, opElse, opFinally) {
			super(loc)
			/** @type {Block} */
			this.try = _try
			/** @type {Array<Catch>} */
			this.typedCatches = typedCatches
			/**
			opCatchAll.caught should have no type.
			@type {?Catch}
			*/
			this.opCatchAll = opCatchAll
			/** @type {?Block} */
			this.opElse = opElse
			/** @type {?Block} */
			this.opFinally = opFinally
		}

		get allCatches() {
			return cat(this.typedCatches, this.opCatchAll)
		}
	}

	/**
	```catch {caught}
		{block}```
	*/
	export class Catch extends MsAst {
		constructor(loc, caught, block) {
			super(loc)
			/** @type {LocalDeclare} */
			this.caught = caught
			/** @type {Block} */
			this.block = block
		}
	}

// Block
	/** Lines in an indented block. */
	export class Block extends MsAst {
		constructor(loc, opComment, lines) {
			super(loc)
			/** @type {?string} */
			this.opComment = opComment
			/** @type {Array<LineContent>} */
			this.lines = lines
		}
	}

	/** Part of a builder. */
	export class BuildEntry extends MsAst {}

	/** Part of a {@link BlockObj}. */
	export class ObjEntry extends BuildEntry {
		constructor(loc) {
			super(loc)
		}
	}

	/**
	`a. b`
	ObjEntry that produces a new local.
	*/
	export class ObjEntryAssign extends ObjEntry {
		constructor(loc, assign) {
			super(loc)
			/** @type {Assign} */
			this.assign = assign
		}
	}

	/** ObjEntry that does not introduce a new local. */
	export class ObjEntryPlain extends ObjEntry {
		/**
		`{name}.` with no value.
		Takes a local of the same name from outside.
		*/
		static access(loc, name) {
			return new ObjEntryPlain(loc, name, new LocalAccess(loc, name))
		}

		static name(loc, value) {
			return new ObjEntryPlain(loc, 'name', value)
		}

		constructor(loc, name, value) {
			super(loc)
			/** @type {string | Val} */
			this.name = name
			/** @type {Val} */
			this.value = value
		}
	}

	/** `. {value}` or `... {value}` */
	export class BagEntry extends BuildEntry {
		constructor(loc, value, isMany) {
			super(loc)
			/** @type {Val} */
			this.value = value
			/** @type {boolean} */
			this.isMany = isMany
		}
	}

	/** `key` -> `val` */
	export class MapEntry extends BuildEntry {
		constructor(loc, key, val) {
			super(loc)
			/** @type {Val} */
			this.key = key
			/** @type {Val} */
			this.val = val
		}
	}

// Conditionals
	/**
	```if/unless {test}
		{result}```
	*/
	export class Conditional extends LineContent {
		constructor(loc, test, result, isUnless) {
			super(loc)
			/** @type {Val} */
			this.test = test
			/** @type {Block|Val} */
			this.result = result
			/** @type {boolean} */
			this.isUnless = isUnless
		}
	}

	/** `cond {test} {ifTrue} {ifFalse}` */
	export class Cond extends Val {
		constructor(loc, test, ifTrue, ifFalse) {
			super(loc)
			/** @type {Val} */
			this.test = test
			/** @type {Val} */
			this.ifTrue = ifTrue
			/** @type {Val} */
			this.ifFalse = ifFalse
		}
	}

// Fun
	export class FunLike extends Val {
		constructor(loc, args, opRestArg) {
			super(loc)
			/** @type {Array<LocalDeclare>} */
			this.args = args
			/** @type {?LocalDeclare} */
			this.opRestArg = opRestArg
			// TODO: opReturnType should be common too
		}
	}

	/**
	```|:{opDeclareRes} {args} ...{opRestArg}
		{block}```
	*/
	export class Fun extends FunLike {
		constructor(loc, args, opRestArg, block, opts = {}) {
			super(loc, args, opRestArg)
			/** @type {Block} */
			this.block = block
			/** @type {Funs} */
			this.kind = opts.kind || Funs.Plain
			/** @type {?LocalDeclareThis} */
			this.opDeclareThis = opIf(opts.isThisFun, () => LocalDeclare.this(this.loc))
			/** @type {boolean} */
			this.isDo = opts.isDo || false
			/** @type {?Val} */
			this.opReturnType = opts.opReturnType || null
		}
	}
	/**
	Kinds of {@link Fun}.
	@enum {number}
	*/
	export const Funs = {
		/** Regular function (`|`) */
		Plain: 0,
		/** `$|` */
		Async: 1,
		/** `~|` */
		Generator: 2
	}

	export class FunAbstract extends FunLike {
		constructor(loc, args, opRestArg, opReturnType, opComment) {
			super(loc, args, opRestArg)
			/** @type {?Val} */
			this.opReturnType = opReturnType
			/** @type {?string} */
			this.opComment = opComment
		}
	}

	export class Method extends Val {
		constructor(loc, fun) {
			super(loc)
			/** @type {FunLike} */
			this.fun = fun
		}
	}

// Async / Generator

	/** `$ {value} `*/
	export class Await extends Val {
		constructor(loc, value) {
			super(loc)
			/** @type {Val} */
			this.value = value
		}
	}

	/** `yield {opValue}` */
	export class Yield extends Val {
		constructor(loc, opValue = null) {
			super(loc)
			/** @type {?Val} */
			this.opValue = opValue
		}
	}

	/** `yield* {value}` */
	export class YieldTo extends Val {
		constructor(loc, value) {
			super(loc)
			/** @type {Val} */
			this.value = value
		}
	}

// Class
	export class Kind extends Val {
		constructor(loc, superKinds, opComment = null, opDo = null, statics = [], methods = []) {
			super(loc)
			/** @type {Array<Val>} */
			this.superKinds = superKinds
			/** @type {?string} */
			this.opComment = opComment
			/** @type {?ClassKindDo} */
			this.opDo = opDo
			/** @type {Array<MethodImplLike>} */
			this.statics = statics
			/** @type {Array<MethodImplLike>} */
			this.methods = methods
		}
	}

	/**
	```class {opSuperClass}
		{opComment}
		do!
			{opDo}
		static
			{statics}
		{opConstructor}
		{methods}```
	*/
	export class Class extends Val {
		constructor(
			loc, opSuperClass, kinds,
			opComment = null, opDo = null, statics = [], opConstructor = null, methods = []) {
			super(loc)
			/** @type {?Val} */
			this.opSuperClass = opSuperClass
			/** @type {Array<Val>} */
			this.kinds = kinds
			/** @type {?string} */
			this.opComment = opComment
			/** @type {?ClassKindDo} */
			this.opDo = opDo
			/** @type {Array<MethodImplLike>} */
			this.statics = statics
			/** @type {?Constructor} */
			this.opConstructor = opConstructor
			/** @type {Array<MethodImplLike>} */
			this.methods = methods
		}
	}

	/** `do!` part of {@link Class} or {@link Kind}. */
	export class ClassKindDo extends MsAst {
		constructor(loc, block) {
			super(loc)
			/** @type {Block} */
			this.block = block
			/** @type {LocalDeclareFocus} */
			this.declareFocus = LocalDeclare.focus(loc)
		}
	}

	/** `construct! {fun}` */
	export class Constructor extends MsAst {
		constructor(loc, fun, memberArgs) {
			super(loc)
			/** @type {Fun} */
			this.fun = fun
			/** @type {Array<LocalDeclare>} */
			this.memberArgs = memberArgs
		}
	}

	/** Any part of {@link Class.statics} or {@link Class.methods}. */
	export class MethodImplLike extends MsAst {
		constructor(loc, isMy, symbol) {
			super(loc)
			/**
			Used by tools.
			@type {boolean}
			*/
			this.isMy = isMy
			/** @type {string | Val} */
			this.symbol = symbol
		}
	}
	/** `{symbol} {fun}` */
	export class MethodImpl extends MethodImplLike {
		constructor(loc, isMy, symbol, fun) {
			super(loc, isMy, symbol)
			/** @type {Fun} */
			this.fun = fun
		}
	}
	/**
	```get {symbol}
		{block}```
	*/
	export class MethodGetter extends MethodImplLike {
		constructor(loc, isMy, symbol, block) {
			super(loc, isMy, symbol)
			/** @type {Block} */
			this.block = block
			this.declareThis = LocalDeclare.this(loc)
		}
	}
	/**
	```set {symbol}
		{block}```
	*/
	export class MethodSetter extends MethodImplLike {
		constructor(loc, isMy, symbol, block) {
			super(loc, isMy, symbol)
			/** @type {Block} */
			this.block = block
			this.declareThis = LocalDeclare.this(loc)
			this.declareFocus = LocalDeclare.focus(loc)
		}
	}

	/**
	`super {args}`.
	Never a {@link SuperMember}.
	*/
	export class SuperCall extends LineContent {
		constructor(loc, args) {
			super(loc)
			/** @type {Array<Val | Spread>} */
			this.args = args
		}
	}

	/** `super.{name}` or `super."{name}"`. */
	export class SuperMember extends Val {
		constructor(loc, name) {
			super(loc)
			/** @type {string | Val} */
			this.name = name
		}
	}

// Calls
	/** `{called} {args}` */
	export class Call extends Val {
		/** `{tested}:{testType}` */
		static contains(loc, testType, tested) {
			return new Call(loc, new SpecialVal(loc, SpecialVals.Contains), [testType, tested])
		}

		/** `{subbed}[{args}]` */
		static sub(loc, subbed, args) {
			return new Call(loc, new SpecialVal(loc, SpecialVals.Sub), cat(subbed, args))
		}

		/** `del! {subbed}[{args}]` */
		static delSub(loc, subbed, args) {
			return new Call(loc, new SpecialVal(loc, SpecialVals.DelSub), cat(subbed, args))
		}

		constructor(loc, called, args) {
			super(loc)
			/** @type {Val} */
			this.called = called
			/** @type {Array<Val | Spread>} */
			this.args = args
		}
	}

	/** `new {type} {args}` */
	export class New extends Val {
		constructor(loc, type, args) {
			super(loc)
			/** @type {Val} */
			this.type = type
			/** @type {Val | Spread} */
			this.args = args
		}
	}

	/** `...{spreaded}` */
	export class Spread extends MsAst {
		constructor(loc, spreaded) {
			super(loc)
			/** @type {Val} */
			this.spreaded = spreaded
		}
	}

	/** `~{value}` */
	export class Lazy extends Val {
		constructor(loc, value) {
			super(loc)
			/** @type {Val} */
			this.value = value
		}
	}

// Case
	/** `case` */
	export class Case extends LineContent {
		constructor(loc, opCased, parts, opElse) {
			super(loc)
			/**
			Assignee is always a LocalDeclareFocus.
			@type {?AssignSingle}
			*/
			this.opCased = opCased
			/** @type {Array<CasePart>} */
			this.parts = parts
			/** @type {?Block} */
			this.opElse = opElse
		}
	}
	/** Single case in a {@link Case}. */
	export class CasePart extends MsAst {
		constructor(loc, test, result) {
			super(loc)
			/** @type {Val | Pattern} */
			this.test = test
			/** @type {Block} */
			this.result = result
		}
	}

	/** `:{type} {locals}` */
	export class Pattern extends MsAst {
		constructor(loc, type, locals) {
			super(loc)
			/** @type {Val} */
			this.type = type
			/** @type {Array<LocalDeclare>} */
			this.locals = locals
			/** @type {LocalAccess} */
			this.patterned = LocalAccess.focus(loc)
		}
	}

// Switch
	/** `switch` */
	export class Switch extends LineContent {
		constructor(loc, switched, parts, opElse) {
			super(loc)
			/** @type {Val} */
			this.switched = switched
			/** @type {Array<SwitchPart>} */
			this.parts = parts
			/** @type {?Block} */
			this.opElse = opElse
		}
	}
	/**
	Single case in a {@link Switch}.
	Multiple values are specified with `or`.
	*/
	export class SwitchPart extends MsAst {
		constructor(loc, values, result) {
			super(loc)
			/** @type {Array<Val>} */
			this.values = values
			/** @type {Block} */
			this.result = result
		}
	}

// For
	/** `for` */
	export class For extends LineContent {
		constructor(loc, opIteratee, block) {
			super(loc)
			/** @type {?Iteratee} */
			this.opIteratee = opIteratee
			/** @type {Block} */
			this.block = block
		}
	}

	/**
	```$for {opIteratee}
	*/
	export class ForAsync extends Val {
		constructor(loc, iteratee, block) {
			super(loc)
			/** @type {Iteratee} */
			this.iteratee = iteratee
			/** @type {Block} */
			this.block = block
		}
	}

