	*/
	export class With extends Val {
		constructor(loc, declare, value, block) {
			super(loc)
			/** @type {LocalDeclare} */
			this.declare = declare
			/** @type {Val} */
			this.value = value
			/** @type {Block} */
			this.block = block
		}
	}

	/** `&{name}` or `.&{name}` or `{object}.&{name}` */
	export class MemberFun extends Val {
		constructor(loc, opObject, name) {
			super(loc)
			/** @type {?Val} */
			this.opObject = opObject
			/** @type {string | Val} */
			this.name = name
		}
