	/** `&.{name}` */
	export class GetterFun extends Val {
		constructor(loc, name) {
			super(loc)
			/** @type {string | Val} */
			this.name = name
		}
	}

	/** `&({value})` */
	export class SimpleFun extends Val {
		constructor(loc, value) {
			super(loc)
			/** @type {Val} */
			this.value = value
		}
	}

	/** `{start}..{end}` or `{start}...{end}`. */
	export class Range extends Val {
		constructor(loc, start, end, isExclusive) {
			super(loc)
			/** @type {Val} */
