/**
Name of a keyword.
@param {Keywords} kind
@return {string}
*/
export function keywordName(kind) {
	return keywordKindToName.get(kind)
}

export function showKeyword(kind) {
	return code(keywordName(kind))
}

/**
See if the name is a keyword and if so return its kind.
@return {?Keywords}
*/
export function opKeywordKindFromName(name) {
	const kind = keywordNameToKind.get(name)
	return kind === undefined ? null : kind
}

export function opKeywordKindToSpecialValueKind(kind) {
	switch (kind) {
		case Keywords.False:
			return SpecialVals.False
		case Keywords.Name:
			return SpecialVals.Name
		case Keywords.Null:
			return SpecialVals.Null
		case Keywords.True:
			return SpecialVals.True
		case Keywords.Undefined:
			return SpecialVals.Undefined
		default:
			return null
	}
}

/**
Whether `token` is a Group of the given kind.
@param {Groups} groupKind
@param {Token} token
*/
export function isGroup(groupKind, token) {
	return token instanceof Group && token.kind === groupKind
}

/**
Whether `token` is a Keyword of the given kind.
@param {Keywords} keywordKind
@param {Token} token
*/
export function isKeyword(keywordKind, token) {
	return token instanceof Keyword && token.kind === keywordKind
}

/**
Whether `token` is a Keyword of any of the given kinds.
@param {Set} keywordKinds
@param {Token} token
*/
export function isAnyKeyword(keywordKinds, token) {
	return token instanceof Keyword && keywordKinds.has(token.kind)
}

/** Whether `token` is a Keyword whose value can be used as a property name. */
export function isNameKeyword(token) {
	return isAnyKeyword(nameKeywords, token)
}

/** Whether `token` is a reserved word. */
export function isReservedKeyword(token) {
	return token instanceof Keyword && token.kind < firstNonReservedKeyword
}

import {check} from './context'

/**
Results of {@link verify}.
This is only the data needed by {@link transpile}.
*/
export default class VerifyResults {
	constructor() {
		/**
		LocalAccess -> LocalDeclare.
		Needed because lazy accesses must be compiled differently.
		*/
		this.localAccessToDeclare = new Map()
		/**
		LocalDeclare -> Array[LocalAccess].
		Debug locals will not be output if not in debug mode.
		*/
		this.localDeclareToAccesses = new Map()
		/**
		Maps Class/Fun to name if one is appropriate.
		Maps *every* {@link SpecialVals.Name} to the nearest name.
		*/
		this.names = new Map()
		/**
		String -> Set.
		For each path, the names of each builtin imported.
		Like the inverse of context.opts.builtinNameToPath,
		but only includes names actually used.
		*/
		this.builtinPathToNames = new Map()
		/** Values are either MethodImpl or the string 'constructor' */
		this.superCallToMethod = new Map()
		/** Links a constructor to its super! call. */
		this.constructorToSuper = new Map()
		/** Stores verified block kind (see verifyBlock.js) */
		this.blockToKind = new Map()
		/**
		Set of MsAsts that have been marked as being statements.
		Those which are always statements (like Throw) are not marked.
		Use a set of statements because there are usually many more vals than statements.
		*/
		this.statements = new Set()
		/** ObjEntry_s that are module exports */
		this.objEntryExports = new Set()
		/** @type {Modules} */
		this.moduleKind = null
		/** Set of {@link Loop}s with at least one {@link Break} in a {@link Switch}. */
		this.loopsNeedingLabel = new Set()
		/** Set of {@link Break}s that are inside {@link Switch}es. */
		this.breaksInSwitch = new Set()
	}

	/** Gets the LocalDeclare that was verified to be the one accessed. */
	localDeclareForAccess(localAccess) {
		return this.localAccessToDeclare.get(localAccess)
	}

	/** Get closest assignment name to an expression. */
	name(expr) {
		const name = this.names.get(expr)
		check(name !== undefined, expr.loc,
			'Expression must be placed in a position where name can be determined.')
		return name
	}

	/**
	Get closest assignment name to an expression,
	or `null` if none is available.
	*/
	opName(expr) {
		const x = this.names.get(expr)
		return x === undefined ? null : x
	}

	/** Certain expressions (such as `if`) are marked if they are statements. */
	isStatement(expr) {
		return this.statements.has(expr)
	}

	/** What kind of block the verifier determined this to be. */
	blockKind(block) {
		return this.blockToKind.get(block)
	}

	/** Whether an ObjEntry is a module export. */
	isObjEntryExport(objEntry) {
		return this.objEntryExports.has(objEntry)
	}

	constructorHasSuper(ctr) {
		return this.constructorToSuper.has(ctr)
	}

	/** Whether some `break` in this loop is in a `switch`. */
	loopNeedsLabel(loop) {
		return this.loopsNeedingLabel.has(loop)
	}

	/** Returns whether there is a `switch` in between this `break` and its loop. */
	isBreakInSwitch(breakAst) {
		return this.breaksInSwitch.has(breakAst)
	}
}

/** Kinds of {@link Block}. */
export const Blocks = {
	Do: 0,
	Throw: 1,
	Return: 2,
	Bag: 3,
	Map: 4,
	Obj: 5
}

/** Kinds of {@link Module}. */
export const Modules = {
	Do: 0,
	Val: 1,
	Exports: 2,
	Bag: 3,
	Map: 4
}

import {Pos, singleCharLoc} from 'esast/dist/Loc'
import CompileError, {Warning} from '../CompileError'
import PathOptions from './PathOptions'

/** @type {CompileOptions} */
export let options
/**
Array of all warnings produced during compilation.
(Please use {@warn} instead of writing to this directly.)
*/
export let warnings
/** @type {PathOptions} */
export let pathOptions

/**
Write to {@link options} and {@link warnings}.
Remember to call {@link unsetContext}!
*/
export function setContext(_options, filename) {
	options = _options
	pathOptions = new PathOptions(filename)
	warnings = []
}

/** Release {@link options} and {@link warnings} for garbage collection. */
export function unsetContext() {
	options = null
	warnings = null
}

/**
If `cond` is false, {@link fail}.
`loc` and `message` may also be Functions to to get them lazily.
*/
export function check(cond, loc, message) {
	if (!cond) {
		if (loc instanceof Function)
			loc = loc()
		if (message instanceof Function)
			message = message()
		fail(loc, message)
	}
}

/**
Throw a {@link CompileError}.
Parameters are the same as for {@link warn}.
*/
export function fail(loc, message) {
	throw new CompileError(warning(loc, message))
}

/**
Add a new warning.
@param {Loc|Pos} loc
@param {string} message
	Will often contain sequences created by {@link code}.
*/
export function warn(loc, message) {
	warnings.push(warning(loc, message))
}

const warning = (loc, message) => {
	if (loc instanceof Pos)
		loc = singleCharLoc(loc)
	return new Warning(loc, message)
}

import Loc, {StartPos} from 'esast/dist/Loc'
import {check, warn} from '../context'
import {Group, Groups, showGroupKind} from '../Token'
import {assert, isEmpty} from '../util'

let groupStack
export let curGroup

export function setupGroupContext() {
	curGroup = new Group(new Loc(StartPos, null), [], Groups.Block)
	groupStack = []
}

export function tearDownGroupContext(endPos) {
	closeLine(endPos)
	assert(isEmpty(groupStack))
	curGroup.loc.end = endPos
	const res = curGroup
	groupStack = curGroup = null
	return res
}

/*
We only ever write to the innermost Group;
when we close that Group we add it to the enclosing Group and continue with that one.
Note that `curGroup` is conceptually the top of the stack, but is not stored in `stack`.
*/

export function addToCurrentGroup(token) {
	curGroup.subTokens.push(token)
}

function dropGroup() {
	curGroup = groupStack.pop()
}

// Pause writing to curGroup in favor of writing to a sub-group.
// When the sub-group finishes we will pop the stack and resume writing to its parent.
export function openGroup(openPos, groupKind) {
	groupStack.push(curGroup)
	// Contents will be added to by `addToCurrentGroup`.
	// curGroup.loc.end will be written to when closing it.
	curGroup = new Group(new Loc(openPos, null), [], groupKind)
}

export function maybeCloseGroup(closePos, closeKind) {
	if (curGroup.kind === closeKind)
		closeGroupNoCheck(closePos, closeKind)
}

export function closeGroup(closePos, closeKind) {
	check(closeKind === curGroup.kind, closePos, () =>
		`Trying to close ${showGroupKind(closeKind)}, ` +
		`but last opened ${showGroupKind(curGroup.kind)}`)
	closeGroupNoCheck(closePos, closeKind)
}

function closeGroupNoCheck(closePos, closeKind) {
	const justClosed = curGroup
	dropGroup()
	justClosed.loc.end = closePos
	switch (closeKind) {
		case Groups.Space: {
			const size = justClosed.subTokens.length
			if (size === 0)
				warn(justClosed.loc, 'Unnecessary space.')
			else
				// Spaced should always have at least two elements.
				addToCurrentGroup(size === 1 ? justClosed.subTokens[0] : justClosed)
			break
		}
		case Groups.Line:
			// Line must have content.
			// This can happen if there was just a comment.
			if (!isEmpty(justClosed.subTokens))
				addToCurrentGroup(justClosed)
			break
		case Groups.Block:
			check(!isEmpty(justClosed.subTokens), closePos, 'Empty block.')
			addToCurrentGroup(justClosed)
			break
		default:
			addToCurrentGroup(justClosed)
	}
}

export function closeSpaceOKIfEmpty(pos) {
	assert(curGroup.kind === Groups.Space)
	if (curGroup.subTokens.length === 0)
		dropGroup()
	else
		closeGroupNoCheck(pos, Groups.Space)
}

export function openParenthesis(loc) {
	openGroup(loc.start, Groups.Parenthesis)
	openGroup(loc.end, Groups.Space)
}

export function openInterpolation(loc) {
	openGroup(loc.start, Groups.Interpolation)
	openGroup(loc.end, Groups.Space)
}

/**
Close a Groups.Interpolation or Groups.Parenthesis,
returning whether it was an interpolation.
*/
export function closeInterpolationOrParenthesis(loc) {
	closeGroupNoCheck(loc.start, Groups.Space)
	const kind = curGroup.kind
	closeGroup(loc.end, kind)
	return kind === Groups.Interpolation
}

export function closeGroupsForDedent(pos) {
	closeLine(pos)
	closeGroup(pos, Groups.Block)
	// It's OK to be missing a closing parenthesis if there's a block. E.g.:
	// a (b
	//	c | no closing paren here
	while (curGroup.kind === Groups.Parenthesis || curGroup.kind === Groups.Space)
		closeGroupNoCheck(pos, curGroup.kind)
}

// When starting a new line, a spaced group is created implicitly.
export function openLine(pos) {
	openGroup(pos, Groups.Line)
	openGroup(pos, Groups.Space)
}

export function closeLine(pos) {
	if (curGroup.kind === Groups.Space)
		closeSpaceOKIfEmpty()
	closeGroup(pos, Groups.Line)
}

// When encountering a space, it both closes and opens a spaced group.
export function space(loc) {
	maybeCloseGroup(loc.start, Groups.Space)
	openGroup(loc.end, Groups.Space)
}

// TODO:ES6 Recursive modules should work, so this should not be necessary.

export let lexQuote

export function load(_) {
	lexQuote = _.lexQuote
}

import './loadLex*'
import {StartPos} from 'esast/dist/Loc'
import {openLine, setupGroupContext, tearDownGroupContext} from './groupContext'
import lexPlain from './lexPlain'
import {pos, setupSourceContext} from './sourceContext'

/**
Lexes the source code into {@link Token}s.
The Mason lexer also groups tokens as part of lexing.
This makes writing a recursive-descent parser easy.
See {@link Group}.

@param {string} sourceString
@return {Group<Groups.Block>}
	Block token representing the whole module.
*/
export default function lex(sourceString) {
	// Algorithm requires trailing newline to close any blocks.
	if (!sourceString.endsWith('\n'))
		sourceString = `${sourceString}\n`

	/*
	Use a 0-terminated string so that we can use `0` as a switch case in lexPlain.
	This is faster than checking whether index === length.
	(If we check past the end of the string we get `NaN`, which can't be switched on.)
	*/
	sourceString = `${sourceString}\0`

	setupGroupContext()
	setupSourceContext(sourceString)

	openLine(StartPos)

	lexPlain(false)

	const endPos = pos()
	return tearDownGroupContext(endPos)
}

import Loc from 'esast/dist/Loc'
import {check} from '../context'
import {Name, Keyword, Keywords, opKeywordKindFromName, showKeyword} from '../Token'
import {ifElse} from '../util'
import {isNameCharacter} from './chars'
import {addToCurrentGroup} from './groupContext'
import {pos, skipRestOfLine, takeWhileWithPrev} from './sourceContext'

/**
This is called *after* having eaten the first character of the name.
@param {Pos} startPos Position of first character.
@param {boolean} isInterpolation
	Whether this is a quote interpolation name like `#foo`.
*/
export default function lexName(startPos, isInterpolation) {
	const name = takeWhileWithPrev(isNameCharacter)
	if (name.endsWith('_')) {
		if (name.length > 1)
			handleNameText(startPos, name.slice(0, name.length - 1), false)
		keyword(pos(), Keywords.Focus)
	} else
		handleNameText(startPos, name, !isInterpolation)
}

function handleNameText(startPos, name, allowSpecialKeywords) {
	ifElse(opKeywordKindFromName(name),
		kind => {
			switch (kind) {
				case Keywords.Region: case Keywords.Todo:
					check(allowSpecialKeywords, startPos, () =>
						`${showKeyword(kind)} is not allowed here.`)
					skipRestOfLine()
					if (kind === Keywords.Region)
						keyword(startPos, Keywords.Region)
					// todo: warn for all todo comments
					break
				default:
					keyword(startPos, kind)
			}
		},
		() => {
			addToCurrentGroup(new Name(new Loc(startPos, pos()), name))
		})
}

function keyword(startPos, kind) {
	addToCurrentGroup(new Keyword(new Loc(startPos, pos()), kind))
}

import Loc, {Pos} from 'esast/dist/Loc'
import {code} from '../../CompileError'
import {check, fail, options, warn} from '../context'
import {NumberLiteral} from '../MsAst'
import {DocComment, Group, Groups, isKeyword, Keyword, Keywords} from '../Token'
import {assert, isEmpty, last} from '../util'
import {Chars, isDigit, isDigitBinary, isDigitHex, isDigitOctal, isNameCharacter, showChar
	} from './chars'
import {addToCurrentGroup, closeGroup, closeGroupsForDedent, closeInterpolationOrParenthesis,
	closeLine, closeSpaceOKIfEmpty, curGroup, openGroup, openLine, openParenthesis, space
	} from './groupContext'
import {lexQuote} from './lex*'
import lexName from './lexName'
import {column, eat, eatRestOfLine, index, line, peek, pos, sourceString, skip, skipNewlines,
	skipRestOfLine, skipWhile, skipWhileEquals, tryEat, tryEat2, tryEat3} from './sourceContext'

/*
In the case of quote interpolation ("a{b}c") we'll recurse back into here.
When isInQuote is true, we will not allow newlines.
*/
export default function lexPlain(isInQuote) {
	// This tells us which indented block we're in.
	// Incrementing it means issuing a GP_OpenBlock and decrementing it means a GP_CloseBlock.
	// Does nothing if isInQuote.
	let indent = 0

	// This is where we started lexing the current token.
	let startColumn
	function startPos() {
		return new Pos(line, startColumn)
	}
	function loc() {
		return new Loc(startPos(), pos())
	}
	function keyword(kind) {
		addToCurrentGroup(new Keyword(loc(), kind))
	}
	function funKeyword(kind) {
		keyword(kind)
		// First arg in its own spaced group
		space(loc())
	}
	function eatAndAddNumber() {
		const startIndex = index - 1

		tryEat(Chars.Hyphen)
		if (peek(-1) === Chars.N0) {
			const p = peek()
			switch (p) {
				case Chars.LetterB: case Chars.LetterO: case Chars.LetterX: {
					skip()
					const isDigitSpecial =
						p === Chars.LetterB ?
						isDigitBinary :
						p === Chars.LetterO ?
						isDigitOctal :
						isDigitHex
					skipWhile(isDigitSpecial)
					break
				}
				case Chars.Dot:
					if (isDigit(peek(1))) {
						skip()
						skipWhile(isDigit)
					}
					break
				default:
			}
		} else {
			skipWhile(isDigit)
			if (peek() === Chars.Dot && isDigit(peek(1))) {
				skip()
				skipWhile(isDigit)
			}
		}

		const str = sourceString.slice(startIndex, index)
		addToCurrentGroup(new NumberLiteral(loc(), str))
	}
	function eatIndent() {
		const optIndent = options.indent()
		if (optIndent === '\t') {
			const indent = skipWhileEquals(Chars.Tab)
			check(peek() !== Chars.Space, pos, 'Line begins in a space')
			return indent
		} else {
			const spaces = skipWhileEquals(Chars.Space)
			check(spaces % optIndent === 0, pos, () =>
				`Indentation spaces must be a multiple of ${optIndent}`)
			return spaces / optIndent
		}
	}
	function handleName() {
		lexName(startPos(), false)
	}

	loop: for (;;) {
		startColumn = column
		const characterEaten = eat()
		// Generally, the type of a token is determined by the first character.
		switch (characterEaten) {
			case Chars.Null:
				break loop
			case Chars.Backtick: case Chars.Quote:
				lexQuote(indent, characterEaten === Chars.Backtick)
				break

			// GROUPS

			case Chars.OpenParenthesis:
				if (tryEat(Chars.CloseParenthesis))
					addToCurrentGroup(new Group(loc(), [], Groups.Parenthesis))
				else
					openParenthesis(loc())
				break
			case Chars.OpenBracket:
				if (tryEat(Chars.CloseBracket))
					addToCurrentGroup(new Group(loc(), [], Groups.Bracket))
				else {
					openGroup(startPos(), Groups.Bracket)
					openGroup(pos(), Groups.Space)
				}
				break
			case Chars.CloseParenthesis:
				if (closeInterpolationOrParenthesis(loc())) {
					assert(isInQuote)
					break loop
				}
				break
			case Chars.CloseBracket:
				closeGroup(startPos(), Groups.Space)
				closeGroup(pos(), Groups.Bracket)
				break
			case Chars.Space:
				space(loc())
				break
			case Chars.Newline: {
				check(!isInQuote, loc, 'Quote interpolation cannot contain newline')
				if (peek(-2) === Chars.Space)
					warn(pos, 'Line ends in a space.')

				// Skip any blank lines.
				skipNewlines()
				const oldIndent = indent
				indent = eatIndent()
				if (indent > oldIndent) {
					check(indent === oldIndent + 1, loc,
						'Line is indented more than once')
					const l = loc()
					// Block at end of line goes in its own spaced group.
					// However, `~` preceding a block goes in a group with it.
					if (isEmpty(curGroup.subTokens) ||
						!isKeyword(Keywords.Lazy, last(curGroup.subTokens))) {
						if (curGroup.kind === Groups.Space)
							closeSpaceOKIfEmpty(l.start)
						openGroup(l.end, Groups.Space)
					}
					openGroup(l.start, Groups.Block)
					openLine(l.end)
				} else {
					const l = loc()
					for (let i = indent; i < oldIndent; i = i + 1)
						closeGroupsForDedent(l.start)
					closeLine(l.start)
					openLine(l.end)
				}
				break
			}
			case Chars.Tab:
				// We always eat tabs in the Newline handler,
				// so this will only happen in the middle of a line.
				fail(loc(), 'Tab may only be used to indent')
				break

			// FUN

			case Chars.Bang:
				if (tryEat(Chars.Bar))
					funKeyword(Keywords.FunDo)
				else
					handleName()
				break
			case Chars.Cash:
				if (tryEat2(Chars.Bang, Chars.Bar))
					funKeyword(Keywords.FunAsyncDo)
				else if (tryEat(Chars.Bar))
					funKeyword(Keywords.FunAsync)
				else
					handleName()
				break
			case Chars.Star:
				if (tryEat2(Chars.Bang, Chars.Bar))
					funKeyword(Keywords.FunGenDo)
				else if (tryEat(Chars.Bar))
					funKeyword(Keywords.FunGen)
				else
					handleName()
				break
			case Chars.Bar:
				if (tryEat(Chars.Space) || tryEat(Chars.Tab)) {
					const text = eatRestOfLine()
					closeSpaceOKIfEmpty(startPos())
					if (!(curGroup.kind === Groups.Line && curGroup.subTokens.length === 0))
						fail(loc,
							`Doc comment must go on its own line. Did you mean ${code('||')}?`)
					addToCurrentGroup(new DocComment(loc(), text))
				} else if (tryEat(Chars.Bar))
					// non-doc comment
					skipRestOfLine()
				else
					funKeyword(Keywords.Fun)
				break

			// NUMBER

			case Chars.Hyphen:
				if (isDigit(peek()))
					// eatAndAddNumber() looks at prev character, so hyphen included.
					eatAndAddNumber()
				else
					handleName()
				break
			case Chars.N0: case Chars.N1: case Chars.N2: case Chars.N3: case Chars.N4:
			case Chars.N5: case Chars.N6: case Chars.N7: case Chars.N8: case Chars.N9:
				eatAndAddNumber()
				break


			// OTHER

			case Chars.Dot: {
				if (peek() === Chars.Space || peek() === Chars.Newline) {
					// Keywords.ObjEntry in its own spaced group.
					// We can't just create a new Group here because we want to
					// ensure it's not part of the preceding or following spaced group.
					closeSpaceOKIfEmpty(startPos())
					keyword(Keywords.ObjAssign)
				} else if (tryEat(Chars.Bar))
					funKeyword(Keywords.FunThis)
				else if (tryEat2(Chars.Bang, Chars.Bar))
					funKeyword(Keywords.FunThisDo)
				else if (tryEat2(Chars.Star, Chars.Bar))
					funKeyword(Keywords.FunThisGen)
				else if (tryEat3(Chars.Star, Chars.Bang, Chars.Bar))
					funKeyword(Keywords.FunThisGenDo)
				else if (tryEat(Chars.Dot))
					if (tryEat(Chars.Dot))
						keyword(Keywords.Dot3)
					else
						keyword(Keywords.Dot2)
				else
					keyword(Keywords.Dot)
				break
			}

			case Chars.Colon:
				if (tryEat(Chars.Equal))
					keyword(Keywords.LocalMutate)
				else
					keyword(Keywords.Colon)
				break

			case Chars.Tick:
				keyword(Keywords.Tick)
				break

			case Chars.Tilde:
				keyword(Keywords.Lazy)
				break

			case Chars.Ampersand:
				keyword(Keywords.Ampersand)
				break

			case Chars.Backslash: case Chars.Caret: case Chars.Comma: case Chars.Percent:
			case Chars.Semicolon:
				fail(loc(), `Reserved character ${showChar(characterEaten)}`)
				break

			default:
				check(isNameCharacter(peek(-1)), loc(), () =>
					`Reserved character ${showChar(peek(-1))}`)
				handleName()
		}
	}
}

import {singleCharLoc} from 'esast/dist/Loc'
import {code} from '../../CompileError'
import {check, warn} from '../context'
import {Groups} from '../Token'
import {assert} from '../util'
import {Chars, isDigit, isNameCharacter} from './chars'
import {addToCurrentGroup, closeGroup, curGroup, openGroup, openInterpolation} from './groupContext'
import lexName from './lexName'
import lexPlain from './lexPlain'
import {eat, peek, pos, skipNewlines, skipWhileEquals, stepBackMany, tryEat, tryEatNewline
	} from './sourceContext'

export default function lexQuote(indent, isRegExp) {
	const quoteIndent = indent + 1

	// Indented quote is characterized by being immediately followed by a newline.
	// The next line *must* have some content at the next indentation.
	const isIndented = tryEatNewline()
	if (isIndented) {
		const actualIndent = skipWhileEquals(Chars.Tab)
		check(actualIndent === quoteIndent, pos,
			'Indented quote must have exactly one more indent than previous line.')
	}

	// Current string literal part of quote we are reading.
	// This is a raw value. If source code has '\n' (2 characters), read has '\n' (2 characters).
	let read = ''
	function add(str) {
		// I've tried pushing character codes to an array and stringifying them later,
		// but this turned out to be better.
		read = `${read}${str}`
	}
	function addChar(char) {
		add(String.fromCharCode(char))
	}

	function maybeOutputRead() {
		if (read !== '') {
			addToCurrentGroup(read)
			read = ''
		}
	}

	function locSingle() {
		return singleCharLoc(pos())
	}

	const groupKind = isRegExp ? Groups.RegExp : Groups.Quote

	openGroup(locSingle().start, groupKind)

	eatChars: for (;;) {
		const char = eat()

		switch (char) {
			case Chars.Backslash: {
				const next = eat()
				// \#, \`, and \" are special because they escape a mason special character,
				// while others are escape sequences.
				if (next === Chars.Hash || next === (isRegExp ? Chars.Backtick : Chars.Quote))
					addChar(next)
				else
					add(`\\${String.fromCharCode(next)}`)
				break
			}
			case Chars.Hash:
				maybeOutputRead()
				if (tryEat(Chars.OpenParenthesis)) {
					const l = locSingle()
					openInterpolation(l)
					lexPlain(true)
					// Returning from lexPlain means that the interpolation was closed.
				} else {
					const startPos = pos()
					const firstChar = eat()
					check(isNameCharacter(firstChar), pos, () =>
						`${code('#')} must be followed by ${code('(')}, ${code('#')}, or a name.`)
					lexName(startPos, true)
				}
				break
			// Don't need `case Chars.Null:` because that's always preceded by a newline.
			case Chars.Newline: {
				const originalPos = pos()
				// Go back to before we ate it.
				originalPos.column = originalPos.column - 1

				check(isIndented, pos, 'Unclosed quote.')
				// Allow extra blank lines.
				const numNewlines = skipNewlines()
				const newIndent = skipWhileEquals(Chars.Tab)
				if (newIndent < quoteIndent) {
					// Indented quote section is over.
					// Undo reading the tabs and newline.
					stepBackMany(originalPos, numNewlines + newIndent)
					assert(peek() === Chars.Newline)
					break eatChars
				} else
					add('\n'.repeat(numNewlines) + '\t'.repeat(newIndent - quoteIndent))
				break
			}
			case Chars.Backtick:
				if (isRegExp)
					if (isIndented)
						addChar(char)
					else
						break eatChars
				else
					// Since these compile to template literals, have to remember to escape.
					add('\\\`')
				break
			case Chars.Quote:
				if (!isRegExp && !isIndented)
					break eatChars
				else
					addChar(char)
				break
			default:
				addChar(char)
		}
	}

	maybeOutputRead()

	if (isRegExp)
		curGroup.flags = lexRegExpFlags()
	else
		warnForSimpleQuote(curGroup)

	closeGroup(pos(), groupKind)
}

function warnForSimpleQuote(quoteGroup) {
	const tokens = quoteGroup.subTokens
	if (tokens.length === 1) {
		const name = tokens[0]
		if (typeof name === 'string' && isName(name))
			warn(pos(), `Quoted text could be a simple quote ${code(`'${name}`)}.`)
	}
}

function isName(str) {
	const cc0 = str.charCodeAt(0)
	if (isDigit(cc0) || cc0 === Chars.Tilde)
		return false
	for (let i = 0; i < str.length; i = i + 1)
		if (!isNameCharacter(str.charCodeAt(i)))
			return false
	return true
}

function lexRegExpFlags() {
	let flags = ''
	for (const ch of [Chars.G, Chars.I, Chars.M, Chars.Y])
		if (tryEat(ch))
			flags = flags + String.fromCharCode(ch)
	return flags
}

import {Pos, StartLine, StartColumn} from 'esast/dist/Loc'
import {Chars} from './chars'

/*
These are kept up-to-date as we iterate through sourceString.
Every access to index has corresponding changes to line and/or column.
This also explains why there are different functions for newlines vs other characters.
*/
export let index
export let line
export let column
export let sourceString

export function setupSourceContext(_sourceString) {
	sourceString = _sourceString
	index = 0
	line = StartLine
	column = StartColumn
}

/*
NOTE: We use character *codes* for everything.
Characters are of type Number and not just Strings of length one.
*/

export function pos() {
	return new Pos(line, column)
}

export function peek(n = 0) {
	return sourceString.charCodeAt(index + n)
}

// May eat a Newline.
// Caller *must* check for that case and increment line!
export function eat() {
	const char = sourceString.charCodeAt(index)
	skip()
	return char
}
export function skip(n = 1) {
	index = index + n
	column = column + n
}

// charToEat must not be Newline.
export function tryEat(charToEat) {
	const canEat = peek() === charToEat
	if (canEat)
		skip()
	return canEat
}

// chars must not be Newline
export function tryEat2(char1, char2) {
	const canEat = peek() === char1 && peek(1) === char2
	if (canEat)
		skip(2)
	return canEat
}

export function tryEat3(char1, char2, char3) {
	const canEat = peek() === char1 && peek(1) === char2 && peek(2) === char3
	if (canEat)
		skip(3)
	return canEat
}

export function tryEatNewline() {
	const canEat = peek() === Chars.Newline
	if (canEat) {
		index = index + 1
		line = line + 1
		column = StartColumn
	}
	return canEat
}

// Caller must ensure that backing up nCharsToBackUp characters brings us to oldPos.
export function stepBackMany(oldPos, nCharsToBackUp) {
	index = index - nCharsToBackUp
	line = oldPos.line
	column = oldPos.column
}

// For takeWhile, takeWhileWithPrev, and skipWhileEquals,
// characterPredicate must *not* accept Newline.
// Otherwise there may be an infinite loop!
export function takeWhile(characterPredicate) {
	return _takeWhileWithStart(index, characterPredicate)
}
export function takeWhileWithPrev(characterPredicate) {
	return _takeWhileWithStart(index - 1, characterPredicate)
}
export function _takeWhileWithStart(startIndex, characterPredicate) {
	skipWhile(characterPredicate)
	return sourceString.slice(startIndex, index)
}

export function skipWhileEquals(char) {
	return skipWhile(_ => _ === char)
}

export function skipRestOfLine() {
	return skipWhile(_ => _ !== Chars.Newline)
}

export function eatRestOfLine() {
	return takeWhile(_ => _ !== Chars.Newline)
}

export function skipWhile(characterPredicate) {
	const startIndex = index
	while (characterPredicate(peek()))
		index = index + 1
	const diff = index - startIndex
	column = column + diff
	return diff
}

// Called after seeing the first newline.
// Returns # total newlines, including the first.
export function skipNewlines() {
	const startLine = line
	line = line + 1
	while (peek() === Chars.Newline) {
		index = index + 1
		line = line + 1
	}
	column = StartColumn
	return line - startLine
}

// Sprinkle checkPos() around to debug line and column tracking errors.
/*
function checkPos() {
	const p = _getCorrectPos()
	if (p.line !== line || p.column !== column)
		throw new Error(`index: ${index}, wrong: ${Pos(line, column)}, right: ${p}`)
}
const _indexToPos = new Map()
function _getCorrectPos() {
	if (index === 0)
		return Pos(StartLine, StartColumn)

	let oldPos, oldIndex
	for (oldIndex = index - 1; ; oldIndex = oldIndex - 1) {
		oldPos = _indexToPos.get(oldIndex)
		if (oldPos !== undefined)
			break
		assert(oldIndex >= 0)
	}
	let newLine = oldPos.line, newColumn = oldPos.column
	for (; oldIndex < index; oldIndex = oldIndex + 1)
		if (sourceString.charCodeAt(oldIndex) === Newline) {
			newLine = newLine + 1
			newColumn = StartColumn
		} else
			newColumn = newColumn + 1

	const p = Pos(newLine, newColumn)
	_indexToPos.set(index, p)
	return p
}
*/

/** Some Mason modules have names that don't work as URl paths. */
export default function manglePath(path) {
	return path.replace(/!/g, 'bang')
	.replace(/@/g, 'at')
	.replace(/\?/g, 'q')
	.replace(/\$/g, 'cash')
}

import Loc from 'esast/dist/Loc'
import {isEmpty, opIf} from '../util'

/**
Represents a slice of the `subTokens` of some {@link Group}.
This is just a view of it, so taking e.g. {@link tail} is O(1).
Most parser functions act on a Slice and call other functions on sub-slices.
*/
export default class Slice {
	/**
	Slice representing all subTokens of a {@link Group}.
	@type {Group} group
	*/
	static group(group) {
		return new Slice(group.subTokens, 0, group.subTokens.length, group.loc)
	}

	/** @private */
	constructor(tokens, start, end, loc) {
		/**
		Array of tokens in use.
		The slice will only use the ones from `start` to `end`.
		(This is more efficient than calling {@link Array#slice} many times.)
		@type {Array<Token>}
		*/
		this._tokens = tokens
		/**
		Inclusive; index of first token in the slice.
		@type {number}
		*/
		this._start = start
		/**
		Exclusive; index of first token *not* in the slice.
		@type {number}
		*/
		this._end = end
		/**
		Slice keeps track of changes to `loc` as sub-slices are made,
		so most parser functions will call `tokens.loc` when constructing the {@link MsAst}.
		@type {Loc}
		*/
		this.loc = loc
	}

	/** Number of tokens. */
	size() {
		return this._end - this._start
	}

	/** True iff there are no tokens left. */
	isEmpty() {
		return this._start === this._end
	}

	// For these methods, caller must ensure non-empty.

	/** First token. */
	head() {
		return this._tokens[this._start]
	}

	/** Slice of first token. */
	headSlice() {
		return Slice.group(this.head())
	}

	/** Second token. */
	second() {
		return this._tokens[this._start + 1]
	}

	/** Last token. */
	last() {
		return this._tokens[this._end - 1]
	}

	/** Second-to-last token. */
	nextToLast() {
		return this._tokens[this._end - 2]
	}

	/** Slice of all but the first token. */
	tail() {
		return this._chopStart(this._start + 1)
	}

	/** Slice of all but the last token. */
	rtail() {
		return this._chopEnd(this._end - 1)
	}

	/**
	Splits on the first token satisfying `splitOn`.
	@splitOn {function(token:Token): boolean}
	@return {?{before: Slice, at: Token, after: Slice}}
	*/
	opSplitOnce(splitOn) {
		for (let i = this._start; i < this._end; i = i + 1) {
			const token = this._tokens[i]
			if (splitOn(token))
				return {
					before: this._chopEnd(i),
					at: token,
					after: this._chopStart(i + 1)
				}
		}
		return null
	}

	/**
	Splits on *every* token satisfying `splitOn`.
	@splitOn {function(token:Token): boolean}
	@return {?{Array<{before: Slice, at: Token>}}
		Last output will not have `at`.
	*/
	opSplitMany(splitOn) {
		let iLast = this._start
		const out = []
		for (let i = this._start; i < this._end; i = i + 1) {
			const token = this._tokens[i]
			if (splitOn(token)) {
				out.push({before: this._chop(iLast, i), at: token})
				iLast = i + 1
			}
		}

		return opIf(!isEmpty(out), () => {
			out.push({before: this._chopStart(iLast)})
			return out
		})
	}

	/** Iterate over every Token. */
	* [Symbol.iterator]() {
		for (let i = this._start; i < this._end; i = i + 1)
			yield this._tokens[i]
	}

	/**
	Iterator over a Slice for every Token.
	Assumes every sub-token is a Slice too; meaning this is a {@link Group.Block} slice.
	*/
	* slices() {
		for (const _ of this)
			yield Slice.group(_)
	}

	/**
	Maps over every Token.
	@param {function(token:Token)} mapper
	*/
	map(mapper) {
		const out = []
		for (const _ of this)
			out.push(mapper(_))
		return out
	}

	/** Maps over a Slice for every Token, as in {@link slices}.	*/
	mapSlices(f) {
		const out = []
		for (const _ of this.slices())
			out.push(f(_))
		return out
	}

	_chop(newStart, newEnd) {
		const loc = new Loc(this._tokens[newStart].loc.start, this._tokens[newEnd - 1].loc.end)
		return new Slice(this._tokens, newStart, newEnd, loc)
	}

	_chopStart(newStart) {
		const loc = newStart === this._end ?
			this.loc :
			new Loc(this._tokens[newStart].loc.start, this.loc.end)
		return new Slice(this._tokens, newStart, this._end, loc)
	}

	_chopEnd(newEnd) {
		const loc = newEnd === this._start ?
			this.loc :
			new Loc(this.loc.start, this._tokens[newEnd - 1].loc.end)
		return new Slice(this._tokens, this._start, newEnd, loc)
	}
}

import {check, fail} from '../context'
import {isKeyword, isReservedKeyword, showKeyword} from '../Token'

/** Throw a {@link CompileError} if `tokens` has content. */
export function checkEmpty(tokens, message) {
	check(tokens.isEmpty(), tokens.loc, message)
}

/** Throw a {@link CompileError} if `tokens` is empty. */
export function checkNonEmpty(tokens, message) {
	check(!tokens.isEmpty(), tokens.loc, message)
}

/** Throw a {@link CompileError} if the token is not the expected keyword. */
export function checkKeyword(keyword, token) {
	check(isKeyword(keyword, token), token.loc, () =>
		`Expected ${showKeyword(keyword)}`)
}

/** Throw a {@link CompileError} about encountering an unparseable token. */
export function unexpected(token) {
	const message = isReservedKeyword(token) ? `Reserved word ${token}.` : `Unexpected ${token}.`
	fail(token.loc, message)
}

import './loadParse*'
import parseModule from './parseModule'
import Slice from './Slice'

/**
This converts a Token tree to a MsAst.
This is a recursive-descent parser, made easier by two facts:
	* We have already grouped tokens.
	* Most of the time, an ast's type is determined by the first token.

There are exceptions such as assignment statements (indicated by a `=` somewhere in the middle).
For those we must iterate through tokens and split.
(See {@link Slice#opSplitOnce} and {@link Slice#opSplitMany}.)

@param {Group<Groups.Block>} rootToken
@return {Module}
*/
export default function parse(rootToken) {
	return parseModule(Slice.group(rootToken))
}

import {check} from '../context'
import {Block, BlockWrap} from '../MsAst'
import {Groups, isGroup, showKeyword} from '../Token'
import {checkEmpty} from './checks'
import {parseLines} from './parseLine'
import tryTakeComment from './tryTakeComment'
import Slice from './Slice'

/** Parse lines in a block and leading doc comment. */
export default function parseBlock(lineTokens) {
	const [opComment, rest] = tryTakeComment(lineTokens)
	return new Block(lineTokens.loc, opComment, parseLines(rest))
}

/**
Tokens on the line before a block, and tokens for the block itself.
@return {[Slice, Slice]}
*/
export function beforeAndBlock(tokens) {
	const [before, opBlock] = beforeAndOpBlock(tokens)
	check(opBlock !== null, tokens.loc, 'Expected an indented block at the end.')
	return [before, opBlock]
}

/**
`beforeAndBlock` that returns `null` for missing block.
@return {[Slice, ?Slice]}
*/
export function beforeAndOpBlock(tokens) {
	if (tokens.isEmpty())
		return [tokens, null]
	else {
		const block = tokens.last()
		return isGroup(Groups.Block, block) ? [tokens.rtail(), Slice.group(block)] : [tokens, null]
	}
}

/** Parse a Block as a single value. */
export function parseBlockWrap(tokens) {
	return new BlockWrap(tokens.loc, parseBlock(tokens))
}

/**
Parse a block, failing if there's something preceding it.
@param {Keywords} keywordKind Keyword that precedes the block. Used for error message.
@param {Slice} tokens Tokens which should have a block at the end.
*/
export function justBlock(keywordKind, tokens) {
	const [before, block] = beforeAndBlock(tokens)
	checkEmpty(before, () =>
		`Did not expect anything between ${showKeyword(keywordKind)} and block.`)
	return block
}

/** Parse a block from the end of `tokens`, failing if there's something preceding it. */
export function parseJustBlock(keywordKind, tokens) {
	return parseBlock(justBlock(keywordKind, tokens))
}

import {check} from '../context'
import {AssignSingle, Case, CasePart, Pattern} from '../MsAst'
import {Groups, isGroup, isKeyword, Keywords, showKeyword} from '../Token'
import {opMap} from '../util'
import {checkEmpty} from './checks'
import {opParseExpr, parseExpr} from './parse*'
import parseBlock, {beforeAndBlock, parseJustBlock} from './parseBlock'
import parseLocalDeclares from './parseLocalDeclares'
import parseSpaced from './parseSpaced'
import Slice from './Slice'

/** Parse a {@link Case}. */
export default function parseCase(casedFromFun, tokens) {
	const [before, block] = beforeAndBlock(tokens)

	let opCased
	if (casedFromFun) {
		checkEmpty(before, 'Can\'t make focus — is implicitly provided as first argument.')
		opCased = null
	} else
		opCased = opMap(opParseExpr(before), _ => AssignSingle.focus(_.loc, _))

	const lastLine = Slice.group(block.last())
	const [partLines, opElse] = isKeyword(Keywords.Else, lastLine.head()) ?
		[block.rtail(), parseJustBlock(Keywords.Else, lastLine.tail())] :
		[block, null]

	const parts = partLines.mapSlices(line => {
		const [before, block] = beforeAndBlock(line)
		return new CasePart(line.loc, parseCaseTest(before), parseBlock(block))
	})
	check(parts.length > 0, tokens.loc, () =>
		`Must have at least 1 non-${showKeyword(Keywords.Else)} test.`)

	return new Case(tokens.loc, opCased, parts, opElse)
}

function parseCaseTest(tokens) {
	const first = tokens.head()
	// Pattern match starts with type test and is followed by local declares.
	// E.g., `:Some val`
	if (isGroup(Groups.Space, first) && tokens.size() > 1) {
		const ft = Slice.group(first)
		if (isKeyword(Keywords.Colon, ft.head())) {
			const type = parseSpaced(ft.tail())
			const locals = parseLocalDeclares(tokens.tail())
			return new Pattern(tokens.loc, type, locals)
		}
	}
	return parseExpr(tokens)
}

import {Class, ClassKindDo, Constructor, Fun} from '../MsAst'
import {isKeyword, Keywords} from '../Token'
import {ifElse} from '../util'
import {opParseExpr, parseExprParts} from './parse*'
import {beforeAndOpBlock, parseJustBlock} from './parseBlock'
import {funArgsAndBlock} from './parseFun'
import parseMethodImpls, {parseStatics} from './parseMethodImpls'
import tryTakeComment from './tryTakeComment'

/** Parse a {@link Class}. */
export default function parseClass(tokens) {
	const [before, opBlock] = beforeAndOpBlock(tokens)
	const {opSuperClass, kinds} = parseClassHeader(before)

	let opComment = null, opDo = null, statics = [], opConstructor = null, methods = []
	const finish = () => new Class(tokens.loc,
			opSuperClass, kinds, opComment, opDo, statics, opConstructor, methods)

	if (opBlock === null)
		return finish()

	const [_opComment, _rest] = tryTakeComment(opBlock)
	opComment = _opComment
	let rest = _rest

	if (rest.isEmpty())
		return finish()

	const line1 = rest.headSlice()
	if (isKeyword(Keywords.Do, line1.head())) {
		const done = parseJustBlock(Keywords.Do, line1.tail())
		opDo = new ClassKindDo(line1.loc, done)
		rest = rest.tail()
	}

	if (rest.isEmpty())
		return finish()

	const line2 = rest.headSlice()
	if (isKeyword(Keywords.Static, line2.head())) {
		statics = parseStatics(line2.tail())
		rest = rest.tail()
	}

	if (rest.isEmpty())
		return finish()

	const line3 = rest.headSlice()
	if (isKeyword(Keywords.Construct, line3.head())) {
		opConstructor = parseConstructor(line3.tail())
		rest = rest.tail()
	}
	methods = parseMethodImpls(rest)

	return finish()
}

function parseClassHeader(tokens) {
	const [extendedTokens, kinds] =
		ifElse(tokens.opSplitOnce(_ => isKeyword(Keywords.Kind, _)),
			({before, after}) => [before, parseExprParts(after)],
			() => [tokens, []])
	const opSuperClass = opParseExpr(extendedTokens)
	return {opSuperClass, kinds}
}

function parseConstructor(tokens) {
	const {args, memberArgs, opRestArg, block} = funArgsAndBlock(tokens, false, true)
	const fun = new Fun(tokens.loc, args, opRestArg, block, {isThisFun: true, isDo: true})
	return new Constructor(tokens.loc, fun, memberArgs)
}

import {check} from '../context'
import {Call} from '../MsAst'
import {unexpected} from './checks'
import {Groups, isGroup, Keywords, showKeyword} from '../Token'
import {parseExprParts, parseSpaced} from './parse*'
import Slice from './Slice'

export default function parseDel(tokens) {
	check(tokens.size() === 1, tokens.loc, () =>
		`${showKeyword(Keywords.Del)} takes only one argument.`)
	const spaced = tokens.head()
	if (!isGroup(Groups.Space, spaced))
		unexpected(spaced)

	const parts = Slice.group(spaced)
	const last = parts.last()
	if (isGroup(Groups.Bracket, last)) {
		const object = parseSpaced(parts.rtail())
		const args = parseExprParts(Slice.group(last))
		return Call.delSub(tokens.loc, object, args)
	} else
		unexpected(spaced)
}

import {check} from '../context'
import {Catch, Except} from '../MsAst'
import {isKeyword, Keywords, showKeyword} from '../Token'
import {checkKeyword} from './checks'
import parseBlock, {beforeAndBlock, justBlock, parseJustBlock} from './parseBlock'
import {parseLocalDeclareOrFocus} from './parseLocalDeclares'

/** Parse an {@link Except}. */
export default function parseExcept(tokens) {
	const lines = justBlock(Keywords.Except, tokens)
	const [_try, rest] = takeTry(lines)
	const [typedCatches, opCatchAll, rest2] = takeTypedCatches(rest)
	const [opElse, rest3] = opTakeElse(rest2)
	const opFinally = parseOpFinally(rest3)
	return new Except(tokens.loc, _try, typedCatches, opCatchAll, opElse, opFinally)
}

function takeTry(lines) {
	const line = lines.headSlice()
	checkKeyword(Keywords.Try, line.head())
	return [parseJustBlock(Keywords.Try, line.tail()), lines.tail()]
}

function takeTypedCatches(lines) {
	const typedCatches = []
	let opCatchAll = null

	while (!lines.isEmpty()) {
		const line = lines.headSlice()
		if (!isKeyword(Keywords.Catch, line.head()))
			break

		const [before, block] = beforeAndBlock(line.tail())
		const caught = parseLocalDeclareOrFocus(before)
		const _catch = new Catch(line.loc, caught, parseBlock(block))

		lines = lines.tail()

		if (caught.opType === null) {
			opCatchAll = _catch
			break
		} else
			typedCatches.push(_catch)
	}
	return [typedCatches, opCatchAll, lines]
}

function opTakeElse(lines) {
	if (lines.isEmpty())
		return [null, lines]

	const line = lines.headSlice()
	const tokenElse = line.head()
	return isKeyword(Keywords.Else, tokenElse) ?
		[parseJustBlock(Keywords.Else, line.tail()), lines.tail()] :
		[null, lines]
}

function parseOpFinally(lines) {
	if (lines.isEmpty())
		return null

	const line = lines.headSlice()
	checkKeyword(Keywords.Finally, line.head())
	check(lines.size() === 1, lines.loc, () =>
		`Nothing may come after ${showKeyword(Keywords.Finally)}.`)
	return parseJustBlock(Keywords.Finally, line.tail())
}

import Loc from 'esast/dist/Loc'
import {check} from '../context'
import {Await, Call, Cond, Conditional, LocalDeclare, Logic, Logics, New, Not, ObjPair, ObjSimple,
	Pipe, SuperCall, With, Yield, YieldTo} from '../MsAst'
import {isAnyKeyword, isKeyword, Keywords, Name, showKeyword} from '../Token'
import {cat, head, ifElse, opIf, tail} from '../util'
import {checkNonEmpty} from './checks'
import {parseClass, parseExcept, parseSingle, parseSwitch} from './parse*'
import parseBlock, {beforeAndBlock, beforeAndOpBlock} from './parseBlock'
import parseCase from './parseCase'
import parseDel from './parseDel'
import parseFor from './parseFor'
import parseFun from './parseFun'
import parseMethod from './parseMethod'
import parseKind from './parseKind'
import {parseLocalDeclare} from './parseLocalDeclares'

/** Parse a {@link Val}. */
export default function parseExpr(tokens) {
	return ifElse(tokens.opSplitMany(_ => isKeyword(Keywords.ObjAssign, _)),
		splits => {
			// Short object form, such as (a. 1, b. 2)
			const first = splits[0].before
			checkNonEmpty(first, () => `Unexpected ${splits[0].at}`)
			const tokensCaller = first.rtail()

			const pairs = []
			for (let i = 0; i < splits.length - 1; i = i + 1) {
				const name = splits[i].before.last()
				check(name instanceof Name, name.loc, () =>
					`Expected a name, not ${name}`)
				const tokensValue = i === splits.length - 2 ?
					splits[i + 1].before :
					splits[i + 1].before.rtail()
				const value = parseExprPlain(tokensValue)
				const loc = new Loc(name.loc.start, tokensValue.loc.end)
				pairs.push(new ObjPair(loc, name.name, value))
			}
			const val = new ObjSimple(tokens.loc, pairs)
			if (tokensCaller.isEmpty())
				return val
			else {
				const parts = parseExprParts(tokensCaller)
				return new Call(tokens.loc, head(parts), cat(tail(parts), val))
			}
		},
		() => parseExprPlain(tokens))
}

export function opParseExpr(tokens) {
	return opIf(!tokens.isEmpty(), () => parseExpr(tokens))
}

/**
Treating tokens separately, parse {@link Val}s.
This is called for e.g. the contents of an array (`[a b c]`).
This is different from {@link parseExpr} because `a b` will parse as 2 different things, not a call.
However, `cond a b c` will still parse as a single expression.
@return {Array<Val>}
*/
export function parseExprParts(tokens) {
	return ifElse(tokens.opSplitOnce(_ => isAnyKeyword(exprSplitKeywords, _)),
		({before, at, after}) => cat(before.map(parseSingle), keywordExpr(at, after)),
		() => tokens.map(parseSingle))
}

/** The keyword `at` groups with everything after it. */
function keywordExpr(at, after) {
	switch (at.kind) {
		case Keywords.And: case Keywords.Or: {
			const kind = at.kind === Keywords.And ? Logics.And : Logics.Or
			return new Logic(at.loc, kind, parseExprParts(after))
		}
		case Keywords.Await:
			return new Await(at.loc, parseExprPlain(after))
		case Keywords.Case:
			return parseCase(false, after)
		case Keywords.Class:
			return parseClass(after)
		case Keywords.Cond:
			return parseCond(after)
		case Keywords.Del:
			return parseDel(after)
		case Keywords.Except:
			return parseExcept(after)
		case Keywords.For: case Keywords.ForAsync: case Keywords.ForBag:
			return parseFor(at.kind, after)
		case Keywords.Fun: case Keywords.FunDo:
		case Keywords.FunThis: case Keywords.FunThisDo:
		case Keywords.FunAsync: case Keywords.FunAsyncDo:
		case Keywords.FunThisAsync: case Keywords.FunThisAsyncDo:
		case Keywords.FunGen: case Keywords.FunGenDo:
		case Keywords.FunThisGen: case Keywords.FunThisGenDo:
			return parseFun(at.kind, after)
		case Keywords.If: case Keywords.Unless:
			return parseConditional(at.kind, after)
		case Keywords.Kind:
			return parseKind(after)
		case Keywords.Method:
			return parseMethod(after)
		case Keywords.New: {
			const parts = parseExprParts(after)
			return new New(at.loc, head(parts), tail(parts))
		}
		case Keywords.Not:
			return new Not(at.loc, parseExprPlain(after))
		case Keywords.Pipe:
			return parsePipe(after)
		case Keywords.Super:
			return new SuperCall(at.loc, parseExprParts(after))
		case Keywords.Switch:
			return parseSwitch(false, after)
		case Keywords.With:
			return parseWith(after)
		case Keywords.Yield:
			return new Yield(at.loc, opIf(!after.isEmpty(), () => parseExprPlain(after)))
		case Keywords.YieldTo:
			return new YieldTo(at.loc, parseExprPlain(after))
		default:
			throw new Error(at.kind)
	}
}


const exprSplitKeywords = new Set([
	Keywords.And, Keywords.Await, Keywords.Case, Keywords.Class, Keywords.Cond, Keywords.Del,
	Keywords.Except, Keywords.For, Keywords.ForAsync, Keywords.ForBag, Keywords.Fun, Keywords.FunDo,
	Keywords.FunThis, Keywords.FunThisDo, Keywords.FunAsync, Keywords.FunAsyncDo,
	Keywords.FunThisAsync, Keywords.FunThisAsyncDo, Keywords.FunGen, Keywords.FunGenDo,
	Keywords.FunThisGen, Keywords.FunThisGenDo, Keywords.If, Keywords.Kind, Keywords.Method,
	Keywords.New, Keywords.Not, Keywords.Or, Keywords.Pipe, Keywords.Super, Keywords.Switch,
	Keywords.Unless, Keywords.With, Keywords.Yield, Keywords.YieldTo
])

function parseExprPlain(tokens) {
	checkNonEmpty(tokens, 'Expected an expression, got nothing.')
	const parts = parseExprParts(tokens)
	return parts.length === 1 ? head(parts) : new Call(tokens.loc, head(parts), tail(parts))
}

function parseCond(tokens) {
	const parts = parseExprParts(tokens)
	check(parts.length === 3, tokens.loc, () =>
		`${showKeyword(Keywords.Cond)} takes exactly 3 arguments.`)
	return new Cond(tokens.loc, ...parts)
}

function parseConditional(kind, tokens) {
	const [before, opBlock] = beforeAndOpBlock(tokens)
	const [condition, result] = ifElse(opBlock,
		_ => [parseExprPlain(before), parseBlock(_)],
		() => {
			const parts = parseExprParts(before)
			check(parts.length === 2, tokens.loc, () =>
				`${showKeyword(kind)} with no block takes exactly 2 arguments.`)
			return parts
		})
	return new Conditional(tokens.loc, condition, result, kind === Keywords.Unless)
}

function parsePipe(tokens) {
	const [before, block] = beforeAndBlock(tokens)
	const val = parseExpr(before)
	const pipes = block.mapSlices(parseExpr)
	return new Pipe(tokens.loc, val, pipes)
}

function parseWith(tokens) {
	const [before, block] = beforeAndBlock(tokens)

	const [val, declare] = ifElse(before.opSplitOnce(_ => isKeyword(Keywords.As, _)),
		({before, after}) => {
			check(after.size() === 1, () =>
				`Expected only 1 token after ${showKeyword(Keywords.As)}.`)
			return [parseExprPlain(before), parseLocalDeclare(after.head())]
		},
		() => [parseExprPlain(before), LocalDeclare.focus(tokens.loc)])

	return new With(tokens.loc, declare, val, parseBlock(block))
}

import {check} from '../context'
import {For, ForAsync, ForBag, Iteratee, LocalDeclare} from '../MsAst'
import {isKeyword, Keywords} from '../Token'
import {ifElse, opIf} from '../util'
import {parseExpr} from './parse*'
import parseBlock, {beforeAndBlock} from './parseBlock'
import {parseLocalDeclaresJustNames} from './parseLocalDeclares'

export default function parseFor(kind, tokens) {
	const [before, block] = beforeAndBlock(tokens)
	const Ctr = kindToCtr.get(kind)
	const opIter = Ctr === ForAsync ?
		parseIteratee(before) :
		opIf(!before.isEmpty(), () => parseIteratee(before))
	return new Ctr(tokens.loc, opIter, parseBlock(block))
}

const kindToCtr = new Map(
	[[Keywords.For, For], [Keywords.ForAsync, ForAsync], [Keywords.ForBag, ForBag]])

function parseIteratee(tokens) {
	const [element, bag] =
		ifElse(tokens.opSplitOnce(_ => isKeyword(Keywords.Of, _)),
			({before, after}) => {
				check(before.size() === 1, before.loc, 'TODO: pattern in for')
				return [parseLocalDeclaresJustNames(before)[0], parseExpr(after)]
			},
			() => [LocalDeclare.focus(tokens.loc), parseExpr(tokens)])
	return new Iteratee(tokens.loc, element, bag)
}

import {Block, Fun, FunAbstract, Funs, LocalDeclare} from '../MsAst'
import {Groups, isAnyKeyword, isGroup, isKeyword, Keywords} from '../Token'
import {head} from '../util'
import {checkNonEmpty} from './checks'
import parseBlock, {beforeAndBlock} from './parseBlock'
import parseCase from './parseCase'
import parseLocalDeclares, {parseLocalDeclareFromSpaced, parseLocalDeclaresAndMemberArgs
	} from './parseLocalDeclares'
import parseSpaced from './parseSpaced'
import parseSwitch from './parseSwitch'
import Slice from './Slice'
import tryTakeComment from './tryTakeComment'

/**
Parse a {@link Fun}.
@param keywordKind {Keywords} A function keyword.
@param {Slice} tokens Rest of the line after the function keyword.
*/
export default function parseFun(keywordKind, tokens) {
	const [isThisFun, isDo, kind] = funKind(keywordKind)
	const {opReturnType, rest} = tryTakeReturnType(tokens)
	const {args, opRestArg, block} = funArgsAndBlock(rest, !isDo)
	return new Fun(tokens.loc, args, opRestArg, block, {kind, isThisFun, isDo, opReturnType})
}

/** Parse a {@link FunLike}. */
export function parseFunLike(keywordKind, tokens) {
	const [isThisFun, isDo, kind] = funKind(keywordKind)
	const {opReturnType, rest} = tryTakeReturnType(tokens)
	const [before, blockLines] = beforeAndBlock(rest)
	const [opComment, restLines] = tryTakeComment(blockLines)

	if (restLines.size() === 1) {
		const h = restLines.headSlice()
		if (h.size() === 1 && isKeyword(Keywords.Abstract, h.head())) {
			const {args, opRestArg} = parseFunLocals(before)
			return new FunAbstract(tokens.loc, args, opRestArg, opReturnType, opComment)
		}
	}

	const {args, opRestArg, block} = funArgsAndBlock(rest, !isDo)
	return new Fun(tokens.loc, args, opRestArg, block, {kind, isThisFun, isDo, opReturnType})
}

/**
Parse function arguments and body.
This also handles the `|case` and `|switch` forms.
@param {Slice} tokens
@param {boolean} isVal Whether this is a `|` as opposed to a `!|`
@param [includeMemberArgs]
	This is for constructors.
	If true, output will include `memberArgs`.
	This is the subset of `args` whose names are prefixed with `.`.
	e.g.: `construct! .x .y`
@return {
	args: Array<LocalDeclare>,
	opRestArg: ?LocalDeclare,
	memberArgs:Array<LocalDeclare>,
	block: Block
}
*/
export function funArgsAndBlock(tokens, isVal, includeMemberArgs = false) {
	checkNonEmpty(tokens, 'Expected an indented block.')
	const h = tokens.head()

	// Might be `|case` or `|switch`
	if (isAnyKeyword(funFocusKeywords, h)) {
		const expr = (h.kind === Keywords.Case ? parseCase : parseSwitch)(true, tokens.tail())
		const args = [LocalDeclare.focus(h.loc)]
		return {args, opRestArg: null, memberArgs: [], block: new Block(tokens.loc, null, [expr])}
	} else {
		const [before, blockLines] = beforeAndBlock(tokens)
		const {args, opRestArg, memberArgs} = parseFunLocals(before, includeMemberArgs)
		const block = parseBlock(blockLines)
		return {args, opRestArg, memberArgs, block}
	}
}

const funFocusKeywords = new Set([Keywords.Case, Keywords.Switch])

function funKind(keywordKind) {
	switch (keywordKind) {
		case Keywords.Fun:
			return [false, false, Funs.Plain]
		case Keywords.FunDo:
			return [false, true, Funs.Plain]
		case Keywords.FunThis:
			return [true, false, Funs.Plain]
		case Keywords.FunThisDo:
			return [true, true, Funs.Plain]
		case Keywords.FunAsync:
			return [false, false, Funs.Async]
		case Keywords.FunAsyncDo:
			return [false, true, Funs.Async]
		case Keywords.FunThisAsync:
			return [true, false, Funs.Async]
		case Keywords.FunThisAsyncDo:
			return [true, true, Funs.Async]
		case Keywords.FunGen:
			return [false, false, Funs.Generator]
		case Keywords.FunGenDo:
			return [false, true, Funs.Generator]
		case Keywords.FunThisGen:
			return [true, false, Funs.Generator]
		case Keywords.FunThisGenDo:
			return [true, true, Funs.Generator]
		default:
			throw new Error(keywordKind)
	}
}

function tryTakeReturnType(tokens) {
	if (!tokens.isEmpty()) {
		const h = tokens.head()
		if (isGroup(Groups.Space, h) && isKeyword(Keywords.Colon, head(h.subTokens)))
			return {
				opReturnType: parseSpaced(Slice.group(h).tail()),
				rest: tokens.tail()
			}
	}
	return {opReturnType: null, rest: tokens}
}

function parseFunLocals(tokens, includeMemberArgs) {
	if (tokens.isEmpty())
		return {args: [], memberArgs: [], opRestArg: null}
	else {
		let rest = tokens, opRestArg = null
		const l = tokens.last()
		if (isGroup(Groups.Space, l)) {
			const g = Slice.group(l)
			if (isKeyword(Keywords.Dot3, g.head())) {
				rest = tokens.rtail()
				opRestArg = parseLocalDeclareFromSpaced(g.tail())
			}
		}
		if (includeMemberArgs) {
			const {declares: args, memberArgs} = parseLocalDeclaresAndMemberArgs(rest)
			return {args, memberArgs, opRestArg}
		} else
			return {args: parseLocalDeclares(rest), opRestArg}
	}
}

import {ClassKindDo, Kind} from '../MsAst'
import {isKeyword, Keywords} from '../Token'
import {parseExprParts} from './parse*'
import {beforeAndOpBlock, parseJustBlock} from './parseBlock'
import parseMethodImpls, {parseStatics} from './parseMethodImpls'
import tryTakeComment from './tryTakeComment'

/** Parse a {@link Kind}. */
export default function parseKind(tokens) {
	const [before, opBlock] = beforeAndOpBlock(tokens)
	const superKinds = parseExprParts(before)

	let opComment = null, opDo = null, statics = [], methods = []
	const finish = () => new Kind(tokens.loc,
		superKinds, opComment, opDo, statics, methods)

	if (opBlock === null)
		return finish()

	const [_opComment, _rest] = tryTakeComment(opBlock)
	opComment = _opComment
	let rest = _rest

	if (rest.isEmpty())
		return finish()

	const line1 = rest.headSlice()
	if (isKeyword(Keywords.Do, line1.head())) {
		const done = parseJustBlock(Keywords.Do, line1.tail())
		opDo = new ClassKindDo(line1.loc, done)
		rest = rest.tail()
	}

	if (rest.isEmpty())
		return finish()

	const line2 = rest.headSlice()
	if (isKeyword(Keywords.Static, line2.head())) {
		statics = parseStatics(line2.tail())
		rest = rest.tail()
	}

	if (rest.isEmpty())
		return finish()

	methods = parseMethodImpls(rest)

	return finish()
}

import {check} from '../context'
import {Assert, AssignSingle, AssignDestructure, BagEntry, Break, Call, Ignore, LocalAccess,
	LocalMutate, MapEntry, MemberSet, ObjEntryAssign, ObjEntryPlain, QuoteSimple, SetSub, Setters,
	SpecialDo, SpecialDos, SpecialVal, SpecialVals, Throw} from '../MsAst'
import {Groups, isGroup, isAnyKeyword, isKeyword, Keyword, keywordName, Keywords, showKeyword
	} from '../Token'
import {ifElse, tail} from '../util'
import {checkEmpty, checkNonEmpty, unexpected} from './checks'
import {justBlock} from './parseBlock'
import parseLocalDeclares, {parseLocalDeclaresJustNames, parseLocalName} from './parseLocalDeclares'
import parseMemberName from './parseMemberName'
import parseName from './parseName'
import parseQuote from './parseQuote'
import {opParseExpr, parseExpr, parseExprParts, parseSpaced} from './parse*'
import Slice from './Slice'

/** Parse the content of a line. */
export default function parseLine(tokens) {
	const loc = tokens.loc
	const head = tokens.head()
	const rest = () => tokens.tail()

	const noRest = () => {
		checkEmpty(rest(), () => `Did not expect anything after ${head}.`)
	}

	// We only deal with mutable expressions here, otherwise we fall back to parseExpr.
	if (head instanceof Keyword)
		switch (head.kind) {
			case Keywords.Assert: case Keywords.Forbid:
				return parseAssert(head.kind === Keywords.Forbid, rest())
			case Keywords.Break:
				return new Break(loc, opParseExpr(rest()))
			case Keywords.Debugger:
				noRest()
				return new SpecialDo(loc, SpecialDos.Debugger)
			case Keywords.Dot3:
				return new BagEntry(loc, parseExpr(rest()), true)
			case Keywords.Ignore:
				return new Ignore(loc, rest().map(parseLocalName))
			case Keywords.ObjAssign:
				return new BagEntry(loc, parseExpr(rest()))
			case Keywords.Pass:
				noRest()
				return []
			case Keywords.Region:
				return parseLines(justBlock(Keywords.Region, rest()))
			case Keywords.Throw:
				return new Throw(loc, opParseExpr(rest()))
			default:
				// fall through
		}

	return ifElse(tokens.opSplitOnce(_ => isAnyKeyword(lineSplitKeywords, _)),
		({before, at, after}) => {
			switch (at.kind) {
				case Keywords.MapEntry:
					return new MapEntry(loc, parseExpr(before), parseExpr(after))
				case Keywords.ObjAssign:
					return parseObjEntry(before, after, loc)
				default:
					return parseAssignLike(before, at, parseExpr(after), loc)
			}
		},
		() => parseExpr(tokens))
}
const lineSplitKeywords = new Set(
	[Keywords.Assign, Keywords.LocalMutate, Keywords.MapEntry, Keywords.ObjAssign])

export function parseLines(lineTokens) {
	const lines = []
	for (const line of lineTokens.slices()) {
		const _ = parseLine(line)
		if (_ instanceof Array)
			lines.push(..._)
		else
			lines.push(_)
	}
	return lines
}

function parseAssignLike(before, at, value, loc) {
	const kind = at.kind

	if (before.size() === 1) {
		const token = before.head()
		// `a.b = c`, `.b = c`, `a."b" = c`, `."b" = c`, `a[b] = c`; and their `:=` variants.
		if (isGroup(Groups.Space, token)) {
			const spaced = Slice.group(token)
			const [assignee, opType] = ifElse(spaced.opSplitOnce(_ => isKeyword(Keywords.Colon, _)),
				({before, after}) => [before, parseExpr(after)],
				() => [spaced, null])

			const last = assignee.last()
			const object = obj =>
				obj.isEmpty() ? LocalAccess.this(obj.loc) : parseSpaced(obj)

			if (isKeyword(Keywords.Dot, assignee.nextToLast())) {
				const name = parseMemberName(last)
				const set = object(assignee.rtail().rtail())
				return new MemberSet(loc, set, name, opType, setKind(at), value)
			} else if (isGroup(Groups.Bracket, last)) {
				const set = object(assignee.rtail())
				const subbeds = parseExprParts(Slice.group(last))
				return new SetSub(loc, set, subbeds, opType, setKind(at), value, loc)
			}
		}
	}

	return kind === Keywords.LocalMutate ?
		parseLocalMutate(before, value, loc) :
		parseAssign(before, kind, value, loc)
}

function parseObjEntry(before, after, loc) {
	if (before.size() === 1) {
		const token = before.head()
		const isName = isKeyword(Keywords.Name, token)
		const value = () => parseExpr(after)

		// Handle `a.` which moves an outer local into an ObjEntry.
		if (after.isEmpty())
			return isName ?
				ObjEntryPlain.name(loc, new SpecialVal(loc, SpecialVals.Name)) :
				ObjEntryPlain.access(loc, parseLocalName(token))
		else if (token instanceof Keyword)
			return new ObjEntryPlain(loc, keywordName(token.kind), value())
		// `"1". 1`
		else if (isGroup(Groups.Quote, token))
			return new ObjEntryPlain(loc, parseQuote(Slice.group(token)), value())
		// 'foo. 1
		else if (isGroup(Groups.Space, token)) {
			const slice = Slice.group(token)
			if (slice.size() === 2 && isKeyword(Keywords.Tick, slice.head())) {
				const name = new QuoteSimple(loc, parseName(slice.second()))
				return new ObjEntryPlain(loc, name, value())
			}
		}
	}

	const assign = parseAssign(before, Keywords.ObjAssign, parseExpr(after), loc)
	return new ObjEntryAssign(loc, assign)
}

function setKind(keyword) {
	switch (keyword.kind) {
		case Keywords.Assign:
			return Setters.Init
		case Keywords.LocalMutate:
			return Setters.Mutate
		default:
			unexpected(keyword)
	}
}

function parseLocalMutate(localsTokens, value, loc) {
	const locals = parseLocalDeclaresJustNames(localsTokens)
	check(locals.length === 1, loc, 'TODO: LocalDestructureMutate')
	return new LocalMutate(loc, locals[0].name, value)
}

function parseAssign(localsTokens, kind, value, loc) {
	const locals = parseLocalDeclares(localsTokens)
	if (locals.length === 1)
		return new AssignSingle(loc, locals[0], value)
	else {
		check(locals.length > 1, localsTokens.loc, 'Assignment to nothing.')
		const kind = locals[0].kind
		for (const _ of locals)
			check(_.kind === kind, _.loc,
				'All locals of destructuring assignment must be of the same kind.')
		return new AssignDestructure(loc, locals, value, kind)
	}
}

function parseAssert(negate, tokens) {
	checkNonEmpty(tokens, () => `Expected something after ${showKeyword(Keywords.Assert)}.`)

	const [condTokens, opThrown] =
		ifElse(tokens.opSplitOnce(_ => isKeyword(Keywords.Throw, _)),
			({before, after}) => [before, parseExpr(after)],
			() => [tokens, null])

	const parts = parseExprParts(condTokens)
	const cond = parts.length === 1 ? parts[0] : new Call(condTokens.loc, parts[0], tail(parts))
	return new Assert(tokens.loc, negate, cond, opThrown)
}

import {check} from '../context'
import {LocalDeclare, LocalDeclares} from '../MsAst'
import {Groups, isGroup, isKeyword, Keywords, Name} from '../Token'
import {opIf} from '../util'
import {checkNonEmpty, checkKeyword} from './checks'
import {parseSpaced} from './parse*'
import Slice from './Slice'

/**
Parse locals (`a` or `a:b`).
@return {Array<LocalDeclare>}
*/
export default function parseLocalDeclares(tokens) {
	return tokens.map(parseLocalDeclare)
}

/**
Parse locals with no types allowed.
@return {Array<LocalDeclare>}
*/
export function parseLocalDeclaresJustNames(tokens) {
	return tokens.map(_ => LocalDeclare.plain(_.loc, parseLocalName(_)))
}

/** Parse a single local declare. */
export function parseLocalDeclare(token) {
	return _parseLocalDeclare(token)
}

/** Parse a single local declare from the tokens in a {@link Groups.Space}. */
export function parseLocalDeclareFromSpaced(tokens) {
	return _parseLocalDeclareFromSpaced(tokens)
}

/**
For constructor. Parse local declares while allowing `.x`-style arguments.
@return {{declares: Array<LocalDeclare>, memberArgs: Array<LocalDeclare>}}
	`memberArgs` is  a subset of `declares`.
*/
export function parseLocalDeclaresAndMemberArgs(tokens) {
	const declares = [], memberArgs = []
	for (const token of tokens) {
		const {declare, isMember} = _parseLocalDeclare(token, true)
		declares.push(declare)
		if (isMember)
			memberArgs.push(declare)
	}
	return {declares, memberArgs}
}

/**
Parse a name for a local variable.
Unlike {@link parseName}, `_` is the only allowed Keyword.
@return {string}
*/
export function parseLocalName(token) {
	if (isKeyword(Keywords.Focus, token))
		return '_'
	else {
		check(token instanceof Name, token.loc, () => `Expected a local name, not ${token}.`)
		return token.name
	}
}

/**
If `tokens` is:
	empty: untyped focus
	`:Type`: typed focus
	`foo` or `foo:Type`: A normal LocalDeclare.
*/
export function parseLocalDeclareOrFocus(tokens) {
	if (tokens.isEmpty())
		return LocalDeclare.focus(tokens.loc)
	else {
		check(tokens.size() === 1, tokens.loc, 'Expected only one local declare.')
		const token = tokens.head()
		if (isGroup(Groups.Space, token)) {
			const slice = Slice.group(token)
			if (isKeyword(Keywords.Colon, slice.head()))
				return LocalDeclare.typedFocus(tokens.loc, parseSpaced(slice.tail()))
		}
		return parseLocalDeclare(token)
	}
}

function _parseLocalDeclare(token, orMember = false) {
	if (isGroup(Groups.Space, token))
		return _parseLocalDeclareFromSpaced(Slice.group(token), orMember)
	else {
		const declare = LocalDeclare.plain(token.loc, parseLocalName(token))
		return orMember ? {declare, isMember: false} : declare
	}
}

function _parseLocalDeclareFromSpaced(tokens, orMember = false) {
	const [rest, kind, isMember] =
		isKeyword(Keywords.Lazy, tokens.head()) ?
			[tokens.tail(), LocalDeclares.Lazy, false] :
			orMember && isKeyword(Keywords.Dot, tokens.head()) ?
			[tokens.tail(), LocalDeclares.Eager, true] :
			[tokens, LocalDeclares.Eager, false]
	const name = parseLocalName(rest.head())
	const rest2 = rest.tail()
	const opType = opIf(!rest2.isEmpty(), () => {
		const colon = rest2.head()
		checkKeyword(Keywords.Colon, colon)
		const tokensType = rest2.tail()
		checkNonEmpty(tokensType, () => `Expected something after ${colon}`)
		return parseSpaced(tokensType)
	})
	const declare = new LocalDeclare(tokens.loc, name, opType, kind)
	return orMember ? {declare, isMember} : declare
}

import {Groups, isGroup} from '../Token'
import {unexpected} from './checks'
import {tryParseName} from './parseName'
import parseQuote from './parseQuote'
import Slice from './Slice'

/**
Parse a plain member (`a.b`) or computed member (`a."b"`).
@param {Token} token
@return {string|Quote}
*/
export default function parseMemberName(token) {
	const name = tryParseName(token)
	if (name !== null)
		return name
	else if (isGroup(Groups.Quote, token))
		return parseQuote(Slice.group(token))
	else
		unexpected(token)
}

import {Method} from '../MsAst'
import {Keywords, showKeyword} from '../Token'
import {checkEmpty} from './checks'
import {parseFunLike} from './parseFun'
import parseMethodSplit from './parseMethodSplit'

export default function parseMethod(tokens) {
	const {before, kind, after} = parseMethodSplit(tokens)
	checkEmpty(before, () =>
		`Did not expect anything between ${showKeyword(Keywords.Method)} and function.`)
	return new Method(tokens.loc, parseFunLike(kind, after))
}

import {MethodImpl, MethodGetter, MethodSetter, QuoteSimple} from '../MsAst'
import {isKeyword, Keywords} from '../Token'
import {parseExpr} from './parse*'
import parseBlock, {beforeAndBlock, justBlock} from './parseBlock'
import parseFun from './parseFun'
import parseMethodSplit from './parseMethodSplit'

export default function parseMethodImpls(tokens) {
	return tokens.mapSlices(parseMethodImpl)
}

export function parseStatics(tokens) {
	return parseMethodImpls(justBlock(Keywords.Static, tokens))
}

function parseMethodImpl(tokens) {
	let head = tokens.head()

	const isMy = isKeyword(Keywords.My, head)
	if (isMy) {
		tokens = tokens.tail()
		head = tokens.head()
	}

	if (isKeyword(Keywords.Get, head)) {
		const [before, block] = beforeAndBlock(tokens.tail())
		return new MethodGetter(tokens.loc, isMy, parseExprOrQuoteSimple(before), parseBlock(block))
	} else if (isKeyword(Keywords.Set, head)) {
		const [before, block] = beforeAndBlock(tokens.tail())
		return new MethodSetter(tokens.loc, isMy, parseExprOrQuoteSimple(before), parseBlock(block))
	} else {
		const {before, kind, after} = parseMethodSplit(tokens)
		const fun = parseFun(kind, after)
		return new MethodImpl(tokens.loc, isMy, parseExprOrQuoteSimple(before), fun)
	}
}

// If symbol is just a quoted name, store it as a string, which is handled specially.
function parseExprOrQuoteSimple(tokens) {
	const expr = parseExpr(tokens)
	return expr instanceof QuoteSimple ? expr.name : expr
}

import {check, pathOptions} from '../context'
import {ImportDo, Import, LocalDeclare, LocalDeclares, Module} from '../MsAst'
import {Groups, isGroup, isKeyword, Keyword, Keywords, showKeyword} from '../Token'
import {ifElse} from '../util'
import {checkEmpty, checkNonEmpty, checkKeyword} from './checks'
import {justBlock} from './parseBlock'
import {parseLines} from './parseLine'
import {parseLocalDeclaresJustNames} from './parseLocalDeclares'
import parseName, {tryParseName} from './parseName'
import Slice from './Slice'
import tryTakeComment from './tryTakeComment'

/**
Parse the whole Token tree.
@param {Slice} tokens
@return {Module}
*/
export default function parseModule(tokens) {
	// Module doc comment must come first.
	const [opComment, rest0] = tryTakeComment(tokens)
	// Import statements must appear in order.
	const [doImports, rest1] = takeImports(Keywords.ImportDo, rest0)
	const [plainImports, rest2] = takeImports(Keywords.Import, rest1)
	const [lazyImports, rest3] = takeImports(Keywords.ImportLazy, rest2)
	const lines = parseLines(rest3)
	const imports = plainImports.concat(lazyImports)
	return new Module(tokens.loc, pathOptions.moduleName(), opComment, doImports, imports, lines)
}

function takeImports(importKeywordKind, lines) {
	if (!lines.isEmpty()) {
		const line = lines.headSlice()
		if (isKeyword(importKeywordKind, line.head()))
			return [parseImports(importKeywordKind, line.tail()), lines.tail()]
	}
	return [[], lines]
}

function parseImports(importKeywordKind, tokens) {
	const lines = justBlock(importKeywordKind, tokens)
	return lines.mapSlices(line => {
		const {path, name} = parseRequire(line.head())
		const rest = line.tail()
		if (importKeywordKind === Keywords.ImportDo) {
			checkEmpty(rest, () =>
				`This is an ${showKeyword(Keywords.ImportDo)}, so you can't import any values.`)
			return new ImportDo(line.loc, path)
		} else {
			const {imported, opImportDefault} =
				parseThingsImported(name, importKeywordKind === Keywords.ImportLazy, rest)
			return new Import(line.loc, path, imported, opImportDefault)
		}
	})
}

function parseThingsImported(name, isLazy, tokens) {
	const importDefault = () =>
		LocalDeclare.untyped(
			tokens.loc,
			name,
			isLazy ? LocalDeclares.Lazy : LocalDeclares.Eager)

	if (tokens.isEmpty())
		return {imported: [], opImportDefault: importDefault()}
	else {
		const [opImportDefault, rest] = isKeyword(Keywords.Focus, tokens.head()) ?
			[importDefault(), tokens.tail()] :
			[null, tokens]
		const imported = parseLocalDeclaresJustNames(rest).map(l => {
			check(l.name !== '_', l.pos, () =>
				`${showKeyword(Keywords.Focus)} not allowed as import name.`)
			if (isLazy)
				l.kind = LocalDeclares.Lazy
			return l
		})
		return {imported, opImportDefault}
	}
}

function parseRequire(token) {
	return ifElse(tryParseName(token),
		name => ({path: name, name}),
		() => {
			check(isGroup(Groups.Space, token), token.loc, 'Not a valid module name.')
			const tokens = Slice.group(token)

			// Take leading dots.
			let rest = tokens
			const parts = []
			const head = rest.head()
			const n = tryTakeNDots(head)
			if (n !== null) {
				parts.push('.')
				for (let i = 1; i < n; i = i + 1)
					parts.push('..')
				rest = rest.tail()
				while (!rest.isEmpty()) {
					const n = tryTakeNDots(rest.head())
					if (n === null)
						break
					for (let i = 0; i < n; i = i + 1)
						parts.push('..')
					rest = rest.tail()
				}
			}

			// Take name, then any number of dot-then-name (`.x`)
			for (;;) {
				checkNonEmpty(rest)
				parts.push(parseName(rest.head()))
				rest = rest.tail()

				if (rest.isEmpty())
					break

				// If there's something left, it should be a dot, followed by a name.
				checkKeyword(Keywords.Dot, rest.head())
				rest = rest.tail()
			}

			return {path: parts.join('/'), name: parts[parts.length - 1]}
		})
}

function tryTakeNDots(token) {
	if (!(token instanceof Keyword))
		return null
	switch (token.kind) {
		case Keywords.Dot:
			return 1
		case Keywords.Dot2:
			return 2
		case Keywords.Dot3:
			return 3
		default:
			return null
	}
}

import {unexpected} from './checks'
import {isNameKeyword, keywordName, Name} from '../Token'

/**
Parse a {@link Name} or {@link Keyword} usable as one.
@return {string}
*/
export default function parseName(token) {
	const name = tryParseName(token)
	if (name === null)
		unexpected(token)
	return name
}

/**
Like {@link parseName} but returns `null` on failure.
@return {?string}
*/
export function tryParseName(token) {
	return token instanceof Name ?
		token.name :
		isNameKeyword(token) ?
		keywordName(token.kind) :
		null
}

import {LocalAccess, MsRegExp, QuotePlain} from '../MsAst'
import {Groups, isGroup, isKeyword, Name, Keywords} from '../Token'
import {assert} from '../util'
import {parseExpr} from './parse*'
import Slice from './Slice'

/** Parse a {@link QuotePlain} from a {@link Groups.Quote}. */
export default function parseQuote(tokens) {
	return new QuotePlain(tokens.loc, parseParts(tokens))
}

/** Parse an {@link MsRegExp} from a {@link Groups.RegExp}. */
export function parseRegExp(tokens, flags) {
	return new MsRegExp(tokens.loc, parseParts(tokens), flags)
}

function parseParts(tokens) {
	return tokens.map(_ => {
		if (typeof _ === 'string')
			return _
		else if (_ instanceof Name)
			return new LocalAccess(_.loc, _.name)
		else if (isKeyword(Keywords.Focus, _))
			return LocalAccess.focus(_.loc)
		else {
			assert(isGroup(Groups.Interpolation, _))
			return parseExpr(Slice.group(_))
		}
	})
}

import {BagSimple, LocalAccess, NumberLiteral, SpecialVal} from '../MsAst'
import {Group, Groups, Name, opKeywordKindToSpecialValueKind, Keyword, Keywords} from '../Token'
import {ifElse} from '../util'
import {unexpected} from './checks'
import {parseBlockWrap} from './parseBlock'
import parseQuote, {parseRegExp} from './parseQuote'
import {parseExpr, parseExprParts, parseSpaced} from './parse*'
import Slice from './Slice'

/** Parse a single token. */
export default function parseSingle(token) {
	const {loc} = token
	if (token instanceof Name)
		return new LocalAccess(loc, token.name)
	else if (token instanceof Group) {
		const slice = Slice.group(token)
		switch (token.kind) {
			case Groups.Space:
				return parseSpaced(slice)
			case Groups.Parenthesis:
				return parseExpr(slice)
			case Groups.Bracket:
				return new BagSimple(loc, parseExprParts(slice))
			case Groups.Block:
				return parseBlockWrap(slice)
			case Groups.Quote:
				return parseQuote(slice)
			case Groups.RegExp:
				return parseRegExp(slice, token.flags)
			// Groups.Interpolation handled by parseQuote
			default:
				throw new Error(token.kind)
		}
	} else if (token instanceof NumberLiteral)
		return token
	else if (token instanceof Keyword)
		switch (token.kind) {
			case Keywords.Focus:
				return LocalAccess.focus(loc)
			default:
				return ifElse(opKeywordKindToSpecialValueKind(token.kind),
					_ => new SpecialVal(loc, _),
					() => unexpected(token))
		}
	else
		unexpected(token)
}

import {code} from '../../CompileError'
import {check, fail} from '../context'
import {Call, GetterFun, Lazy, LocalAccess, Member, MemberFun, QuoteSimple, QuoteTaggedTemplate,
	Range, SimpleFun, Spread, SuperCall, SuperMember} from '../MsAst'
import {Group, Groups, isGroup, isKeyword, Keyword, Keywords, showKeyword} from '../Token'
import {assert, opIf} from '../util'
import {checkEmpty, unexpected} from './checks'
import {parseExpr, parseExprParts} from './parse*'
import parseMemberName from './parseMemberName'
import parseName from './parseName'
import parseQuote from './parseQuote'
import parseSingle from './parseSingle'
import Slice from './Slice'

/**
Parse tokens in a {@link Groups.Space}.
@return {Val}
*/
export default function parseSpaced(tokens) {
	const h = tokens.head(), rest = tokens.tail()
	if (h instanceof Keyword)
		switch (h.kind) {
			case Keywords.Ampersand: {
				const h2 = rest.head()
				if (isGroup(Groups.Parenthesis, h2))
					return new SimpleFun(tokens.loc, parseExpr(Slice.group(h2)))
				else if (isKeyword(Keywords.Dot, h2)) {
					const tail = rest.tail()
					const h3 = tail.head()
					const fun = new GetterFun(h3.loc, parseMemberName(h3))
					return parseSpacedFold(fun, tail.tail())
				} else {
					const fun = new MemberFun(h2.loc, null, parseMemberName(h2))
					return parseSpacedFold(fun, rest.tail())
				}
			}
			case Keywords.Dot: {
				const h2 = rest.head()
				if (isKeyword(Keywords.Ampersand, h2)) {
					const tail = rest.tail()
					const h3 = tail.head()
					const name = parseMemberName(h3)
					const fun = new MemberFun(h2.loc, LocalAccess.this(h2.loc), name)
					return parseSpacedFold(fun, tail.tail())
				} else {
					const name = parseMemberName(rest.head())
					const member = new Member(h.loc, LocalAccess.this(h.loc), name)
					return parseSpacedFold(member, rest.tail())
				}
			}
			case Keywords.Dot3:
				return new Spread(tokens.loc, parseSpacedFold(parseSingle(rest.head()), rest.tail))
			case Keywords.Lazy:
				return new Lazy(h.loc, parseSpaced(rest))
			case Keywords.Super: {
				// TODO: handle sub here as well
				const h2 = rest.head()
				if (isKeyword(Keywords.Dot, h2)) {
					const tail = rest.tail()
					const sup = new SuperMember(h2.loc, parseMemberName(tail.head()))
					return parseSpacedFold(sup, tail.tail())
				} else if (isGroup(Groups.Parenthesis, h2) && Slice.group(h2).isEmpty()) {
					const x = new SuperCall(h2.loc, [], true)
					return parseSpacedFold(x, rest.tail())
				} else
					fail(`Expected ${showKeyword(Keywords.Dot)} or ${code('()')} ` +
						`after ${showKeyword(Keywords.Super)}`)
			}
			case Keywords.Tick: {
				const h2 = rest.head()
				const quote = new QuoteSimple(h2.loc, parseName(h2))
				return parseSpacedFold(quote, rest.tail())
			}
			case Keywords.Colon:
				return Call.contains(h.loc, parseSpaced(rest), LocalAccess.focus(h.loc))
			default:
				// fall through
		}
	return parseSpacedFold(parseSingle(h), rest)
}

function parseSpacedFold(start, rest) {
	let acc = start
	for (let i = rest._start; i < rest._end; i = i + 1) {
		function restVal() {
			return parseSpaced(rest._chopStart(i + 1))
		}

		const token = rest._tokens[i]
		const loc = token.loc
		if (token instanceof Keyword)
			switch (token.kind) {
				case Keywords.Ampersand:
					if (i === rest._end - 1)
						unexpected(token)
					i = i + 1
					acc = new MemberFun(token.loc, acc, parseMemberName(rest._tokens[i]))
					break
				case Keywords.Dot: {
					// If this were the last one,
					// it would not be a Keywords.Dot but a Keywords.ObjAssign
					assert(i < rest._end - 1)
					i = i + 1
					acc = new Member(token.loc, acc, parseMemberName(rest._tokens[i]))
					break
				}
				case Keywords.Dot2:
					check(i < rest._end - 1, token.loc, () =>
						`Use ${showKeyword(Keywords.Dot3)} for infinite ranges.`)
					return new Range(token.loc, acc, restVal(), false)
				case Keywords.Dot3:
					return new Range(token.loc, acc, opIf(i < rest._end - 1, restVal), true)
				case Keywords.Focus:
					acc = new Call(token.loc, acc, [LocalAccess.focus(loc)])
					break
				case Keywords.Colon:
					return Call.contains(token.loc, restVal(), acc)
				default:
					unexpected(token)
			}
		else if (token instanceof Group) {
			const slice = Slice.group(token)
			switch (token.kind) {
				case Groups.Bracket:
					acc = Call.sub(loc, acc, parseExprParts(slice))
					break
				case Groups.Parenthesis:
					checkEmpty(slice, () => `Use ${code('(a b)')}, not ${code('a(b)')}`)
					acc = new Call(loc, acc, [])
					break
				case Groups.Quote:
					acc = new QuoteTaggedTemplate(loc, acc, parseQuote(slice))
					break
				default:
					unexpected(token)
			}
		} else
			unexpected(token)
	}
	return acc
}

import {check} from '../context'
import {LocalAccess, Switch, SwitchPart} from '../MsAst'
import {isKeyword, Keywords, showKeyword} from '../Token'
import {checkEmpty} from './checks'
import {parseExpr, parseExprParts} from './parse*'
import parseBlock, {beforeAndBlock, parseJustBlock} from './parseBlock'
import Slice from './Slice'

/** Parse a {@link Switch}. */
export default function parseSwitch(switchedFromFun, tokens) {
	const [before, block] = beforeAndBlock(tokens)

	if (switchedFromFun)
		checkEmpty(before, 'Value to switch on is `_`, the function\'s implicit argument.')
	const switched = switchedFromFun ? LocalAccess.focus(tokens.loc) : parseExpr(before)

	const lastLine = Slice.group(block.last())
	const [partLines, opElse] = isKeyword(Keywords.Else, lastLine.head()) ?
		[block.rtail(), parseJustBlock(Keywords.Else, lastLine.tail())] :
		[block, null]

	const parts = partLines.mapSlices(line => {
		const [before, block] = beforeAndBlock(line)
		return new SwitchPart(line.loc, parseExprParts(before), parseBlock(block))
	})
	check(parts.length > 0, tokens.loc, () =>
		`Must have at least 1 non-${showKeyword(Keywords.Else)} test.`)
	return new Switch(tokens.loc, switched, parts, opElse)
}

import {DocComment} from '../Token'
import {assert, isEmpty} from '../util'

/**
Takes DocComment lines and puts them into a comment.
@return {?string}
*/
export default function tryTakeComment(lines) {
	const comments = []
	let rest = lines

	while (!rest.isEmpty()) {
		const hs = rest.headSlice()
		const h = hs.head()
		if (!(h instanceof DocComment))
			break

		assert(hs.size() === 1)
		comments.push(h)
		rest = rest.tail()
	}

	return [isEmpty(comments) ? null : comments.map(_ => _.text).join('\n'), rest]
}

import esRender, {renderWithSourceMap} from 'esast/dist/render'
import {options, pathOptions} from './context'

/** Renders the transpiled Ast. */
export default function render(esAst) {
	return options.includeSourceMap() ?
		renderWithSourceMap(esAst, pathOptions.modulePath(), `./${pathOptions.jsBaseName()}`) :
		{code: esRender(esAst), sourceMap: ''}
}

import {Funs} from '../MsAst'

export let verifyResults
/** Whether we are in an async/generator function. */
export let funKind
export let nextDestructuredId

export function setup(_verifyResults) {
	verifyResults = _verifyResults
	funKind = Funs.Plain
	nextDestructuredId = 0
}

export function tearDown() {
	// Release for garbage collection.
	verifyResults = null
}

export function getDestructuredId() {
	const _ = nextDestructuredId
	nextDestructuredId = nextDestructuredId + 1
	return _
}

export function withFunKind(newFunKind, func) {
	const oldFunKind = funKind
	funKind = newFunKind
	const _ = func()
	funKind = oldFunKind
	return _
}

import {ArrayExpression, ArrowFunctionExpression, AssignmentExpression, BinaryExpression,
	BlockStatement, BreakStatement, CallExpression, ClassBody, ClassExpression,
	ConditionalExpression, DebuggerStatement, ForOfStatement, ForStatement, FunctionExpression,
	Identifier, IfStatement, LabeledStatement, Literal, LogicalExpression, MemberExpression,
	MethodDefinition, NewExpression, ObjectExpression, Property, ReturnStatement, SpreadElement,
