import {FunctionExpression, MethodDefinition, Property} from 'esast/dist/ast'
import {propertyIdOrLiteral} from 'esast/dist/util'
import {MethodGetter, MethodImpl, QuoteAbstract} from '../MsAst'
import {DeclareLexicalThis, IdFocus} from './ast-constants'
import {msCall, t0, t1} from './util'

/** Transpile method to a MethodDefinition in a class. */
export function transpileMethodToDefinition(_, isStatic) {
	const {computed, key, kind, value} = methodParams(_, 'method')
	return new MethodDefinition(key, value, kind, isStatic, computed)
}

/** Transpile method to a property of an object. */
export function transpileMethodToProperty(_) {
	const {computed, isImpl, key, kind, value} = methodParams(_, 'init')
	return new Property(kind, key, value, computed, isImpl)
}

function methodParams(_, defaultKind) {
	const computed = !(typeof _.symbol === 'string')
	const isImpl = _ instanceof MethodImpl
	return {
		computed,
		isImpl,
		key: computed ?
			_.symbol instanceof QuoteAbstract ? t0(_.symbol) : msCall('symbol', t0(_.symbol)) :
			propertyIdOrLiteral(_.symbol),
		kind: isImpl ? defaultKind : _ instanceof MethodGetter ? 'get' : 'set',
		value: isImpl ? t0(_.fun) : getSetFun(_)
	}
}

function getSetFun(_) {
	const args = _ instanceof MethodGetter ? [] : [IdFocus]
	return new FunctionExpression(null, args, t1(_.block, DeclareLexicalThis))
}

import {ArrayExpression, ArrowFunctionExpression, AssignmentExpression, BinaryExpression,
	BlockStatement, CallExpression, ExpressionStatement, Identifier, IfStatement, Literal, Program,
	VariableDeclaration, VariableDeclarator, ReturnStatement, UnaryExpression
	} from 'esast/dist/ast'
import {identifier, loc, member, toStatement} from 'esast/dist/util'
import {options, pathOptions} from '../context'
import manglePath from '../manglePath'
import {Import, LocalDeclare} from '../MsAst'
import {cat, flatMap, isEmpty, last, opIf, opMap, rtail} from '../util'
import {Modules} from '../VerifyResults'
import {DeclareBuiltBag, DeclareBuiltMap, ExportsDefault, IdBuilt, IdExports} from './ast-constants'
import {verifyResults} from './context'
import {idForDeclareCached, lazyWrap, makeDestructureDeclarators, msCall, t0, tLines} from './util'

export default function transpileModule() {
	const body = moduleBody(verifyResults.moduleKind, this.lines)

	const imports = this.imports.filter(_ => _.path !== 'global')

	for (const [path, imported] of verifyResults.builtinPathToNames)
		if (path !== 'global') {
			const importedDeclares = []
			let opImportDefault = null
			const defaultName = last(path.split('/'))
			for (const name of imported) {
				const declare = LocalDeclare.plain(this.loc, name)
				if (name === defaultName)
					opImportDefault = declare
				else
					importedDeclares.push(declare)
			}
			imports.push(new Import(this.loc, path, importedDeclares, opImportDefault))
		}

	const amd = amdWrapModule(this.doImports, imports, body)

	return new Program(cat(
		opIf(options.includeUseStrict(), () => UseStrict),
		opIf(options.includeAmdefine(), () => AmdefineHeader),
		toStatement(amd)))
}

function moduleBody(kind, lines) {
	switch (kind) {
		case Modules.Do: case Modules.Exports:
			return tLines(lines)
		case Modules.Val: {
			const a = tLines(rtail(lines))
			const b = t0(last(lines))
			return cat(a, exportDefault(b))
		}
		case Modules.Bag: case Modules.Map: {
			const declare = kind === Modules.Bag ? DeclareBuiltBag : DeclareBuiltMap
			return cat(declare, tLines(lines), exportDefault(IdBuilt))
		}
		default:
			throw new Error(verifyResults.moduleKind)
	}
}

export function exportNamedOrDefault(val, name) {
	if (name === pathOptions.moduleName())
		return exportDefault(val)
	else
		return exportNamed(val, name)
}

function exportNamed(val, name) {
	return new AssignmentExpression('=', member(IdExports, name), val)
}
function exportDefault(val) {
	return new AssignmentExpression('=', ExportsDefault, val)
}


function amdWrapModule(doImports, imports, body) {
	const shouldImportBoot = options.importBoot()

	const allImports = doImports.concat(imports)
	const allImportPaths = allImports.map(_ => manglePath(_.path))

	const arrImportPaths = new ArrayExpression(cat(
		LitStrExports,
		opIf(shouldImportBoot, () => new Literal(options.bootPath())),
		allImportPaths.map(_ => new Literal(_))))

	const importToIdentifier = new Map()
	const importIdentifiers = []
	for (let i = 0; i < allImports.length; i = i + 1) {
		const _ = allImports[i]
		const id = identifier(`${pathBaseName(_.path)}_${i}`)
		importIdentifiers.push(id)
		importToIdentifier.set(_, id)
	}

	const importArgs = cat(IdExports, opIf(shouldImportBoot, () => IdBoot), importIdentifiers)

	const doBoot = opIf(shouldImportBoot, () =>
		new ExpressionStatement(msCall('getModule', IdBoot)))

	const importDos = doImports.map(_ =>
		loc(new ExpressionStatement(msCall('getModule', importToIdentifier.get(_))), _.loc))

	// Extracts imported values from the modules.
	const opDeclareImportedLocals = opIf(!isEmpty(imports),
		() => new VariableDeclaration('let',
			flatMap(imports, _ => importDeclarators(_, importToIdentifier.get(_)))))

	const fullBody = new BlockStatement(cat(
		doBoot, importDos, opDeclareImportedLocals, body, ReturnExports))

	const lazyBody =
		options.lazyModule() ?
			new BlockStatement([new ExpressionStatement(
				new AssignmentExpression('=', ExportsGet,
					msCall('lazy', new ArrowFunctionExpression([], fullBody))))]) :
			fullBody

	return new CallExpression(IdDefine,
		[arrImportPaths, new ArrowFunctionExpression(importArgs, lazyBody)])
}

function pathBaseName(path) {
	return path.substr(path.lastIndexOf('/') + 1)
}

function importDeclarators({imported, opImportDefault}, moduleIdentifier) {
	// TODO: Could be neater about this
	const isLazy = (isEmpty(imported) ? opImportDefault : imported[0]).isLazy()
	const value = msCall(isLazy ? 'lazyGetModule' : 'getModule', moduleIdentifier)

	const importedDefault = opMap(opImportDefault, def => {
		const defexp = msCall('getDefaultExport', moduleIdentifier)
		const val = isLazy ? lazyWrap(defexp) : defexp
		return loc(new VariableDeclarator(idForDeclareCached(def), val), def.loc)
	})

	const importedDestruct = isEmpty(imported) ? null :
		makeDestructureDeclarators(imported, isLazy, value, true, false)

	return cat(importedDefault, importedDestruct)
}

const IdBoot = new Identifier('_boot')
const IdDefine = new Identifier('define')
const ExportsGet = member(IdExports, '_get')
const LitStrExports = new Literal('exports')
const ReturnExports = new ReturnStatement(IdExports)
const UseStrict = new ExpressionStatement(new Literal('use strict'))

// if (typeof define !== 'function') var define = require('amdefine')(module)
const AmdefineHeader = new IfStatement(
	new BinaryExpression('!==',
		new UnaryExpression('typeof', IdDefine),
		new Literal('function')),
	new VariableDeclaration('var', [
		new VariableDeclarator(IdDefine, new CallExpression(
			new CallExpression(new Identifier('require'), [new Literal('amdefine')]),
			[new Identifier('module')]))]))

import {ArrowFunctionExpression, BlockStatement, CallExpression, ExpressionStatement,
	FunctionExpression, Identifier, Literal, MemberExpression, NewExpression, ReturnStatement,
	ThrowStatement, VariableDeclarator, VariableDeclaration, YieldExpression} from 'esast/dist/ast'
import mangleIdentifier from 'esast/dist/mangle-identifier'
import {loc, toStatement} from 'esast/dist/util'
import {member} from 'esast/dist/util'
import {options} from '../context'
import {Block, Funs, QuoteAbstract} from '../MsAst'
import {assert, cat, opIf, opMap, toArray} from '../util'
import {IdFocus, GlobalError} from './ast-constants'
import {funKind, getDestructuredId, verifyResults} from './context'

export function t0(expr) {
	return loc(expr.transpile(), expr.loc)
}
export function t1(expr, arg) {
	return loc(expr.transpile(arg), expr.loc)
}
export function t2(expr, arg, arg2) {
	return loc(expr.transpile(arg, arg2))
}
export function t3(expr, arg, arg2, arg3) {
	return loc(expr.transpile(arg, arg2, arg3), expr.loc)
}
export function tLines(exprs) {
	const out = []
	for (const expr of exprs) {
		const ast = expr.transpile()
		if (ast instanceof Array)
			// Ignore produces 0 statements and Region produces many.
			for (const _ of ast)
				out.push(toStatement(_))
		else
			out.push(loc(toStatement(ast), expr.loc))
	}
	return out
}

export function accessLocalDeclare(localDeclare) {
	const id = idForDeclareCached(localDeclare)
	return localDeclare.isLazy() ? msCall('unlazy', id) : new Identifier(id.name)
}

export function declare(localDeclare, val) {
	return new VariableDeclaration('let',
		[new VariableDeclarator(idForDeclareCached(localDeclare), val)])
}

const declareToId = new WeakMap()
export function idForDeclareCached(localDeclare) {
	let _ = declareToId.get(localDeclare)
	if (_ === undefined) {
		_ = new Identifier(mangleIdentifier(localDeclare.name))
		declareToId.set(localDeclare, _)
	}
	return _
}

export function opTypeCheckForLocalDeclare(localDeclare) {
	// TODO: Way to typecheck lazies
	return opIf(!localDeclare.isLazy(), () =>
		opMap(localDeclare.opType, type =>
			new ExpressionStatement(msCall(
				'checkContains',
				t0(type),
				accessLocalDeclare(localDeclare),
				new Literal(localDeclare.name)))))
}

export function throwErrorFromString(message) {
	// TODO:ES6 Should be able to use IdError
	return new ThrowStatement(
		new NewExpression(new Identifier('Error'), [new Literal(message)]))
}

export function makeDeclarator(assignee, value, valueIsAlreadyLazy) {
	const {name, opType} = assignee
	const isLazy = assignee.isLazy()
	// TODO: assert(assignee.opType === null)
	// or TODO: Allow type check on lazy value?
	value = isLazy ? value : maybeWrapInCheckContains(value, opType, name)
	const val = isLazy && !valueIsAlreadyLazy ? lazyWrap(value) : value
	assert(isLazy || !valueIsAlreadyLazy)
	return new VariableDeclarator(idForDeclareCached(assignee), val)
}

export function maybeWrapInCheckContains(ast, opType, name) {
	return options.includeChecks() && opType !== null ?
		msCall('checkContains', t0(opType), ast, new Literal(name)) :
		ast
}

export function doThrow(thrown) {
	return new ThrowStatement(thrown instanceof QuoteAbstract ?
		new NewExpression(GlobalError, [t0(thrown)]) :
		t0(thrown))
}

export function transpileName(name) {
	return typeof name === 'string' ? new Literal(name) : t0(name)
}

export function memberStringOrVal(object, memberName) {
	return typeof memberName === 'string' ?
		member(object, memberName) :
		new MemberExpression(object, t0(memberName))
}

export function lazyWrap(value) {
	return msCall('lazy', new ArrowFunctionExpression([], value))
}

const IdMs = new Identifier('_ms')
export function msCall(name, ...args) {
	return new CallExpression(member(IdMs, name), args)
}

export function msMember(name) {
	return member(IdMs, name)
}

export function makeDestructureDeclarators(assignees, isLazy, value, isModule) {
	const id = getDestructuredId()
	const destructuredName = `_$${id}`
	const idDestructured = new Identifier(destructuredName)
	const declarators = assignees.map(assignee => {
		const get = getMember(idDestructured, assignee.name, isLazy, isModule)
		return makeDeclarator(assignee, get, isLazy)
	})
	// Getting lazy module is done by ms.lazyGetModule.
	const val = isLazy && !isModule ? lazyWrap(value) : value
	return cat(new VariableDeclarator(idDestructured, val), declarators)
}
function getMember(astObject, gotName, isLazy, isModule) {
	return isLazy ?
		msCall('lazyProp', astObject, new Literal(gotName)) :
		isModule && options.includeChecks() ?
		msCall('get', astObject, new Literal(gotName)) :
		member(astObject, gotName)
}

/** Wraps a block (with `return` statements in it) in an IIFE. */
export function blockWrap(block) {
	const thunk = funKind === Funs.Plain ?
		new ArrowFunctionExpression([], block) :
		new FunctionExpression(null, [], block, true)
	return callPreservingFunKind(new CallExpression(thunk, []))
}

/** Create a focus fun returning `value` and call it on `calledOn`, preserving generator/async. */
export function callFocusFun(value, calledOn) {
	const fun = funKind === Funs.Plain ?
		new ArrowFunctionExpression([IdFocus], value) :
		new FunctionExpression(
			null, [IdFocus], new BlockStatement([new ReturnStatement(value)]), true)
	return callPreservingFunKind(new CallExpression(fun, [calledOn]))
}

/**
Call a function created by `blockWrap` or `callFocusFun`.
This looks like:
	Funs.Plain: `(_ => foo(_))(1)`.
	Funs.Generator, Funs.Async: `yield* function*(_) { return foo(_) }(1)`
*/
function callPreservingFunKind(call) {
	return funKind === Funs.Plain ? call : new YieldExpression(call, true)
}

export function blockWrapIfBlock(value) {
	const ast = t0(value)
	return value instanceof Block ? blockWrap(ast) : ast
}

/** Wraps a statement in an IIFE if its MsAst is a value. */
export function blockWrapIfVal(ast, statement) {
	return verifyResults.isStatement(ast) ?
		statement :
		blockWrap(new BlockStatement(toArray(statement)))
}

export function focusFun(value) {
	return new ArrowFunctionExpression([IdFocus], value)
}

export function plainLet(identifier, value) {
	return new VariableDeclaration('let', [new VariableDeclarator(identifier, value)])
}

/** Whether every element in `array` is equal. */
export function allSame(array, mapper) {
	if (isEmpty(array))
		return true
	const val = mapper(array[0])
	for (let i = 1; i < array.length; i = i + 1)
		if (mapper(array[i]) !== val)
			return false
	return true
}

/**
Used to ensure that the compiler is programmed correctly.
If an assertion fails, it's a bug in mason-compile.
For errors in source code, see {@link check}.
*/
export function assert(cond) {
	if (!cond)
		throw new Error('Assertion failed.')
}

/**
Combine many values into one array.
Arrays are flattened and nulls are skipped.
So `cat([1, 2], null, 3)` is `[1, 2, 3]`.
*/
export function cat(...parts) {
	const out = []
	for (const _ of parts)
		if (_ instanceof Array)
			out.push(..._)
		else if (_ !== null)
			out.push(_)
	return out
}

/**
Like `Array.prototype.map` but `mapper` should return Arrays,
which are flattened to a single Array.
*/
export function flatMap(mapped, mapper) {
	const out = []
	for (let i = 0; i < mapped.length; i = i + 1)
		out.push(...mapper(mapped[i], i))
	return out
}

/** Like `Array.prototype.map`, but if `opMapper` returns null, that entry is skipped. */
export function flatOpMap(array, opMapper) {
	const out = []
	for (const em of array) {
		const _ = opMapper(em)
		if (_ !== null)
			out.push(_)
	}
	return out
}

/** First element of an Array. */
export function head(array) {
	assert(!isEmpty(array))
	return array[0]
}

/** Take a different action depending on whether `op` is null. */
export function ifElse(op, ifSome, ifNone) {
	return op === null ? ifNone() : ifSome(op)
}

/**
Implement a method `methodName` on many types at once.
@param {object} types Object containing many types.
@param {string} methodName
@param {object} impls
	Keys are names of types in `types`.
	Values are implementations of the method.
*/
export function implementMany(types, methodName, impls) {
	for (const name in impls)
		types[name].prototype[methodName] = impls[name]
}

/** False iff there are any elements in the array. */
export function isEmpty(array) {
	return array.length === 0
}

/** Element at the end of an Array. */
export function last(array) {
	assert(!isEmpty(array))
	return array[array.length - 1]
}

/** Do `action` if `op` is not null. */
export function opEach(op, action) {
	if (op !== null)
		action(op)
}

/**
Create an optional value, which is `null` unless `cond`.
@param {boolean} cond
@param {Function} makeOp
*/
export function opIf(cond, makeOp) {
	return cond ? makeOp() : null
}

/** Call a function on an optional value, but only if it's not null. */
export function opMap(op, mapper) {
	return op === null ? null : mapper(op)
}

/** If an optional value is `null`, replace it. */
export function opOr(op, or) {
	return op === null ? or() : op
}

/** Iterate over an Array in reverse. */
export function* reverseIter(array) {
	for (let i = array.length - 1; i >= 0; i = i - 1)
		yield array[i]
}

/** All but the last element of an Array. */
export function rtail(array) {
	assert(!isEmpty(array))
	return array.slice(0, array.length - 1)
}

/** All but the first element of an Array. */
export function tail(array) {
	assert(!isEmpty(array))
	return array.slice(1)
}

/** Wrap value in an Array if it's not already one. */
export function toArray(value) {
	return value instanceof Array ? value : [value]
}

import {check, warn} from '../context'
import * as MsAstTypes from '../MsAst'
import {Keywords, showKeyword} from '../Token'
import {cat, ifElse, implementMany, isEmpty, last, opOr} from '../util'
import {Blocks} from '../VerifyResults'
import autoBlockKind from './autoBlockKind'
import {results} from './context'

const SK = {
	/** Must be a statement. */
	Do: 0,
	/** Must be an expression. */
	Val: 1
}
/** Statement Kind. */
export default SK

/** This MsAst must be a statement. */
export function checkDo(_, sk) {
	check(sk === SK.Do, _.loc,
		'This can only be used as a statement, but appears in expression context.')
}

/** This MsAst must be a value. */
export function checkVal(_, sk) {
	if (sk === SK.Do)
		warn(_.loc, 'Value appears in statement context, so it does nothing.')
}

/**
This is an MsAst that is sometimes a statement, sometimes an expression.
Mark it using `sk` so that it can transpile correctly.
*/
export function markStatement(_, sk) {
	if (sk === SK.Do)
		results.statements.add(_)
}

/**
Infers whether the last line of a module is a statement or a value.
Prefers to make it a value, such as in the case of a Call.
*/
export function getSK(_) {
	return opOr(_.opSK(), () => SK.Val)
}

// `null` means can't determine whether this must be a statement or value.
implementMany(MsAstTypes, 'opSK', {
	Do() { return SK.Do },
	Val() { return SK.Val },
	Call() { return null },
	Yield() { return null },
	YieldTo() { return null },
	Block() {
		return autoBlockKind(this.lines, this.loc) === Blocks.Return ?
			isEmpty(this.lines) ? SK.Do : last(this.lines).opSK() :
			SK.Val
	},
	Conditional() { return this.result.opSK() },
	Except() {
		const catches = this.allCatches.map(_ => _.block)
		// If there's opElse, `try` is always SK.Do and `else` may be SK.Val.
		const parts = ifElse(this.opElse, _ => cat(_, catches), () => cat(this.try, catches))
		// opFinally is always SK.Do.
		return compositeSK(this.loc, parts)
	},
	For() {
		// If opForSK is null, there are no breaks, so this is an infinite loop.
		return opOr(this.block.opForSK(), () => SK.Do)
	},
	Case: caseSwitchSK,
	Switch: caseSwitchSK
})

function caseSwitchSK() {
	return compositeSK(this.loc, caseSwitchParts(this))
}

implementMany(MsAstTypes, 'opForSK', {
	default() { return null },
	Break() {
		return this.opValue === null ? SK.Do : SK.Val
	},
	Block() { return isEmpty(this.lines) ? null : composite(this.loc, 'opForSK', this.lines) },
	Conditional() { return this.result.opForSK() },
	Case: caseSwitchForSK,
	Except() {
		const catches = this.allCatches.map(_ => _.block)
		// Do look at opFinally for break statements.
		return compositeForSK(this.loc, cat(this.try, catches, this.opElse, this.opFinally))
	},
	Switch: caseSwitchForSK
})

function caseSwitchForSK() {
	return compositeForSK(this.loc, caseSwitchParts(this))
}

function caseSwitchParts(_) {
	return cat(_.parts.map(_ => _.result), _.opElse)
}

function compositeSK(loc, parts) {
	return composite(loc, 'opSK', parts,
		'Can\'t tell if this is a statement. Some parts are statements but others are values.')
}

/**
This handles the rare case where a 'for' loop is the last line of a module.
The error occurs if it looks like:

	for
		switch 0
			0
				break 1
			else
				break

Meaning that it can't be determined whether it's a statement or value.
*/
function compositeForSK(loc, parts) {
	return composite(loc, 'opForSK', parts, () =>
		`Can't tell if ${showKeyword(Keywords.For)} is a statement. ` +
		`Some ${showKeyword(Keywords.Break)}s have a value, others don't.`)
}

function composite(loc, method, parts, errorMessage) {
	let sk = parts[0][method]()
	for (let i = 1; i < parts.length; i = i + 1) {
		const otherSK = parts[i][method]()
		if (sk === null)
			sk = otherSK
		else
			check(otherSK === null || otherSK === sk, loc, errorMessage)
	}
	return sk
}

import {check} from '../context'
import {BagEntry, MapEntry, ObjEntry, Throw} from '../MsAst'
import {isEmpty, last, opOr} from '../util'
import {Blocks} from '../VerifyResults'

export default function autoBlockKind(lines, loc) {
	return opOr(opBlockBuildKind(lines, loc), () =>
		!isEmpty(lines) && last(lines) instanceof Throw ? Blocks.Throw : Blocks.Return)
}

export function opBlockBuildKind(lines, loc) {
	let isBag = false, isMap = false, isObj = false
	for (const line of lines)
		if (line instanceof BagEntry)
			isBag = true
		else if (line instanceof MapEntry)
			isMap = true
		else if (line instanceof ObjEntry)
			isObj = true

	check(!(isBag && isMap) && !(isMap && isObj) && !(isBag && isObj), loc,
		'Block has mixed bag/map/obj entries — can not infer type.')

	return isBag ? Blocks.Bag : isMap ? Blocks.Map : isObj ? Blocks.Obj : null
}

import {Funs} from '../MsAst'
import VerifyResults from '../VerifyResults'
import {withBlockLocals} from './locals'
import SK from './SK'

/** Map from names to LocalDeclares. */
export let locals
/** Locals that don't have to be accessed. */
export let okToNotUse
export let opLoop
/**
Locals for this block.
These are added to locals when entering a Function or lazy evaluation.
In:
	a = |
		b
	b = 1
`b` will be a pending local.
However:
	a = b
	b = 1
will fail to verify, because `b` comes after `a` and is not accessed inside a function.
It would work for `~a is b`, though.
*/
export let pendingBlockLocals
/**
Kind of function we are currently in.
(Funs.Plain if not in a function.)
*/
export let funKind
/** Current method we are in, or a Constructor, or null. */
export let method
/** @type {VerifyResults} */
export let results
/** Name of the closest AssignSingle. */
export let name
/**
Whether we're in a `switch` inside of a `for`.
If there's a `break` statement, the loop will need a label.
*/
export let isInSwitch

export function setup() {
	locals = new Map()
	pendingBlockLocals = []
	funKind = Funs.Plain
	okToNotUse = new Set()
	opLoop = null
	method = null
	results = new VerifyResults()
}

// Release for garbage collection.
export function tearDown() {
	locals = okToNotUse = opLoop = pendingBlockLocals = method = results = null
}

export function withInFunKind(newFunKind, action) {
	const oldFunKind = funKind
	funKind = newFunKind
	action()
	funKind = oldFunKind
}

export function withLoop(newLoop, action) {
	const oldLoop = opLoop
	opLoop = newLoop
	action()
	opLoop = oldLoop
}

export function withMethod(newMethod, action) {
	const oldMethod = method
	method = newMethod
	action()
	method = oldMethod
}

export function withName(newName, action) {
	const oldName = name
	name = newName
	action()
	name = oldName
}

/** Can't break out of loop inside of IIFE. */
export function withIife(action) {
	withLoop(null, () => {
		withInSwitch(false, action)
	})
}

export function withIifeIf(cond, action) {
	if (cond)
		withIife(action)
	else
		action()
}

/** The value form of some expressions need to be wrapped in an IIFE. */
export function withIifeIfVal(sk, action) {
	withIifeIf(sk === SK.Val, action)
}

// TODO:ES6 Shouldn't need this
export function setPendingBlockLocals(val) {
	pendingBlockLocals = val
}

export function withInSwitch(newInSwitch, action) {
	const oldInSwitch = isInSwitch
	isInSwitch = newInSwitch
	action()
	isInSwitch = oldInSwitch
}

export function withFun(funKind, action) {
	withBlockLocals(() => {
		withInFunKind(funKind, () => {
			withIife(action)
		})
	})
}

import {code} from '../../CompileError'
import {check, fail, warn} from '../context'
import {isEmpty} from '../util'
import {locals, okToNotUse, results, pendingBlockLocals, setPendingBlockLocals} from './context'

export function deleteLocal(localDeclare) {
	locals.delete(localDeclare.name)
}

export function setLocal(localDeclare) {
	locals.set(localDeclare.name, localDeclare)
}

export function accessLocal(access, name) {
	const declare = getLocalDeclare(name, access.loc)
	setDeclareAccessed(declare, access)
}

export function setDeclareAccessed(declare, access) {
	results.localDeclareToAccesses.get(declare).push(access)
}

// For expressions affecting lineNewLocals, they will be registered before being verified.
// So, LocalDeclare.verify just the type.
// For locals not affecting lineNewLocals, use this instead of just declare.verify()
export function verifyLocalDeclare(localDeclare) {
	registerLocal(localDeclare)
	localDeclare.verify()
}

export function registerLocal(localDeclare) {
	results.localDeclareToAccesses.set(localDeclare, [])
}

export function registerAndPlusLocal(localDeclare, action) {
	registerLocal(localDeclare)
	plusLocal(localDeclare, action)
}

export function plusLocal(addedLocal, action) {
	const shadowed = locals.get(addedLocal.name)
	locals.set(addedLocal.name, addedLocal)
	action()
	if (shadowed === undefined)
		deleteLocal(addedLocal)
	else
		setLocal(shadowed)
}

// Should have verified that addedLocals all have different names.
export function plusLocals(addedLocals, action) {
	const shadowedLocals = []
	for (const _ of addedLocals) {
		const shadowed = locals.get(_.name)
		if (shadowed !== undefined)
			shadowedLocals.push(shadowed)
		setLocal(_)
	}

	action()

	addedLocals.forEach(deleteLocal)
	shadowedLocals.forEach(setLocal)
}

export function verifyAndPlusLocal(addedLocal, action) {
	verifyLocalDeclare(addedLocal)
	plusLocal(addedLocal, action)
}

export function verifyAndPlusLocals(addedLocals, action) {
	addedLocals.forEach(verifyLocalDeclare)
	const names = new Set()
	for (const _ of addedLocals) {
		check(!names.has(_.name), _.loc, () => `Duplicate local ${code(_.name)}`)
		names.add(_.name)
	}
	plusLocals(addedLocals, action)
}

export function withBlockLocals(action) {
	const oldPendingBlockLocals = pendingBlockLocals
	setPendingBlockLocals([])
	plusLocals(oldPendingBlockLocals, action)
	setPendingBlockLocals(oldPendingBlockLocals)
}

function getLocalDeclare(name, accessLoc) {
	const declare = locals.get(name)
	if (declare === undefined)
		failMissingLocal(accessLoc, name)
	return declare
}

export function failMissingLocal(loc, name) {
	const showLocals = code(Array.from(locals.keys()).join(' '))
	fail(loc, `No such local ${code(name)}.\nLocals are:\n${showLocals}.`)
}

export function warnUnusedLocals() {
	for (const [local, accesses] of results.localDeclareToAccesses)
		if (isEmpty(accesses) && local.name !== 'built' && !okToNotUse.has(local))
			warn(local.loc, `Unused local variable ${code(local.name)}.`)
}

import {check} from '../context'
import {name, okToNotUse, results} from './context'
import SK from './SK'

/** Mark a LocalDeclare as OK to not use. */
export function makeUseOptional(localDeclare) {
	okToNotUse.add(localDeclare)
}

/**
For Asts that use the focus by default, make it OK to not use the variable when it's the focus.
(If the user specified a name, they intended to use it.)
*/
export function makeUseOptionalIfFocus(localDeclare) {
	if (localDeclare.name === '_')
		makeUseOptional(localDeclare)
}

/**
Verify each of asts.
@param {Array<MsAst>} asts
@param {SK} [sk] Optional SK of each ast.
*/
export function verifyEach(asts, sk) {
	for (const _ of asts)
		_.verify(sk)
}

/**
Verify opAst if it exists.
@param {?MsAst} opAst
@param [arg] Argument to pass to _.verify. Usually an {@link SK}.
*/
export function verifyOp(opAst, arg) {
	if (opAst !== null)
		opAst.verify(arg)
}

/** Verify if it's not a string. */
export function verifyName(_) {
	if (typeof _ !== 'string')
		_.verify(SK.Val)
}

export function setName(expr) {
	results.names.set(expr, name)
}

export function verifyNotLazy(localDeclare, message) {
	check(!localDeclare.isLazy(), localDeclare.loc, message)
}

import {code} from '../../CompileError'
import {check, fail, options, pathOptions, warn} from '../context'
import * as MsAstTypes from '../MsAst'
import {Block, Class, Constructor, For, ForBag, Fun, Funs, Kind, LocalDeclare, Method, Pattern
	} from '../MsAst'
import {Keywords, showKeyword} from '../Token'
import {assert, cat, ifElse, implementMany, isEmpty, opEach} from '../util'
import {funKind, isInSwitch, locals, method, opLoop, results, setup, tearDown, withFun, withIife,
	withIifeIf, withIifeIfVal, withInFunKind, withInSwitch, withMethod, withLoop, withName
	} from './context'
import {accessLocal, failMissingLocal, plusLocals, registerAndPlusLocal, setDeclareAccessed,
	setLocal, verifyAndPlusLocal, verifyAndPlusLocals, verifyLocalDeclare, warnUnusedLocals,
	withBlockLocals} from './locals'
import SK, {checkDo, checkVal, getSK, markStatement} from './SK'
import {makeUseOptional, makeUseOptionalIfFocus, setName, verifyEach, verifyName, verifyNotLazy,
	verifyOp} from './util'
import verifyBlock, {verifyDoBlock, verifyModuleLines} from './verifyBlock'

/**
Generates information needed during transpiling, the VerifyResults.
Also checks for existence of local variables and warns for unused locals.
@param {MsAst} msAst
*/
export default function verify(msAst) {
	setup()
	msAst.verify()
	warnUnusedLocals()
	const res = results
	tearDown()
	return res
}

implementMany(MsAstTypes, 'verify', {
	Assert(sk) {
		checkDo(this, sk)
		this.condition.verify(SK.Val)
		verifyOp(this.opThrown, SK.Val)
	},

	AssignSingle(sk) {
		checkDo(this, sk)
		withName(this.assignee.name, () => {
			const doV = () => {
				/*
				Fun and Class only get name if they are immediately after the assignment.
				so in `x = $after-time 1000 |` the function is not named.
				*/
				if (this.value instanceof Class ||
					this.value instanceof Fun ||
					this.value instanceof Method ||
					this.value instanceof Kind)
					setName(this.value)

				// Assignee registered by verifyLines.
				this.assignee.verify()
				this.value.verify(SK.Val)
			}
			if (this.assignee.isLazy())
				withBlockLocals(doV)
			else
				doV()
		})
	},

	AssignDestructure(sk) {
		checkDo(this, sk)
		// Assignees registered by verifyLines.
		verifyEach(this.assignees)
		this.value.verify(SK.Val)
	},

	Await(_sk) {
		check(funKind === Funs.Async, this.loc, () =>
			`Cannot ${showKeyword(Keywords.Await)} outside of async function.`)
		this.value.verify(SK.Val)
	},

	BagEntry(sk) {
		checkDo(this, sk)
		accessLocal(this, 'built')
		this.value.verify(SK.Val)
	},

	BagSimple(sk) {
		checkVal(this, sk)
		verifyEach(this.parts, SK.Val)
	},

	Block: verifyBlock,

	BlockWrap(sk) {
		checkVal(this, sk)
		withIife(() => this.block.verify(sk))
	},

	Break(sk) {
		checkDo(this, sk)
		verifyOp(this.opValue, SK.Val)
		check(opLoop !== null, this.loc, 'Not in a loop.')
		const loop = opLoop

		if (loop instanceof For)
			if (results.isStatement(loop))
				check(this.opValue === null, this.loc, () =>
					`${showKeyword(Keywords.Break)} with value is only valid in ` +
					`${showKeyword(Keywords.For)} in expression position.`)
			else
				check(this.opValue !== null, this.loc, () =>
					`${showKeyword(Keywords.For)} in expression position must ` +
					`${showKeyword(Keywords.Break)} with a value.`)
		else {
			// (ForAsync isn't really a loop)
			assert(loop instanceof ForBag)
			check(this.opValue === null, this.loc, () =>
				`${showKeyword(Keywords.Break)} in ${showKeyword(Keywords.ForBag)} ` +
				`may not have value.`)
		}

		if (isInSwitch) {
			results.loopsNeedingLabel.add(loop)
			results.breaksInSwitch.add(this)
		}
	},

	Call(_sk) {
		this.called.verify(SK.Val)
		verifyEach(this.args, SK.Val)
	},

	Case(sk) {
		markStatement(this, sk)
		withIifeIfVal(sk, () => {
			const doIt = () => {
				verifyEach(this.parts, sk)
				verifyOp(this.opElse, sk)
			}
			ifElse(this.opCased,
				_ => {
					_.verify(SK.Do)
					verifyAndPlusLocal(_.assignee, doIt)
				},
				doIt)
		})
	},

	CasePart(sk) {
		if (this.test instanceof Pattern) {
			this.test.type.verify(SK.Val)
			this.test.patterned.verify(SK.Val)
			verifyAndPlusLocals(this.test.locals, () => this.result.verify(sk))
		} else {
			this.test.verify(SK.Val)
			this.result.verify(sk)
		}
	},

	Catch(sk) {
		// No need to do anything with `sk` except pass it to my block.
		makeUseOptionalIfFocus(this.caught)
		verifyNotLazy(this.caught, 'Caught error can not be lazy.')
		verifyAndPlusLocal(this.caught, () => {
			this.block.verify(sk)
		})
	},

	Class(sk) {
		checkVal(this, sk)
		verifyOp(this.opSuperClass, SK.Val)
		verifyEach(this.kinds, SK.Val)
		verifyOp(this.opDo)
		verifyEach(this.statics)
		verifyOp(this.opConstructor, this.opSuperClass !== null)
		verifyEach(this.methods)
		// name set by AssignSingle
	},

	ClassKindDo() {
		verifyAndPlusLocal(this.declareFocus, () => this.block.verify(SK.Do))
	},

	Cond(sk) {
		// Could be a statement if both results are.
		this.test.verify(SK.Val)
		this.ifTrue.verify(sk)
		this.ifFalse.verify(sk)
	},

	Conditional(sk) {
		markStatement(this, sk)
		this.test.verify(SK.Val)
		withIifeIf(this.result instanceof Block && sk === SK.Val, () => {
			this.result.verify(sk)
		})
	},

	Constructor(classHasSuper) {
		makeUseOptional(this.fun.opDeclareThis)
		withMethod(this, () => {
			this.fun.verify(SK.Val)
		})

		const superCall = results.constructorToSuper.get(this)

		if (classHasSuper)
			check(superCall !== undefined, this.loc, () =>
				`Constructor must contain ${showKeyword(Keywords.Super)}`)
		else
			check(superCall === undefined, () => superCall.loc, () =>
				`Class has no superclass, so ${showKeyword(Keywords.Super)} is not allowed.`)

		for (const _ of this.memberArgs)
			setDeclareAccessed(_, this)
	},

	Except(sk) {
		markStatement(this, sk)
		if (this.opElse === null)
			this.try.verify(sk)
		else {
			plusLocals(verifyDoBlock(this.try), () => this.opElse.verify(sk))
			if (isEmpty(this.allCatches))
				warn(this.loc,
					`${showKeyword(Keywords.Else)} must come after ${showKeyword(Keywords.Catch)}.`)
		}

		if (isEmpty(this.allCatches) && this.opFinally === null)
			warn(this.loc, `${showKeyword(Keywords.Except)} is pointless without ` +
				`${showKeyword(Keywords.Catch)} or ${showKeyword(Keywords.Finally)}.`)

		verifyEach(this.typedCatches, sk)
		verifyOp(this.opCatchAll, sk)
		verifyOp(this.opFinally, SK.Do)
	},

	For(sk) {
		markStatement(this, sk)
		verifyFor(this)
	},

	ForAsync(sk) {
		markStatement(this, sk)
		check(sk !== SK.Do || funKind === Funs.Async, this.loc, () =>
			`${showKeyword(Keywords.ForAsync)} as statement must be inside an async function.`)

		withVerifyIteratee(this.iteratee, () => {
			withFun(Funs.Async, () => {
				// Default block to returning a value, but OK if it doesn't.
				// If a statement, statement, the compiled code will make a Promise
				// that resolves to an array full of `undefined`.
				this.block.verify(getSK(this.block))
			})
		})
	},

	ForBag(sk) {
		checkVal(this, sk)
		verifyAndPlusLocal(this.built, () => verifyFor(this))
	},

	Fun(sk) {
		checkVal(this, sk)
		check(this.opReturnType === null || !this.isDo, this.loc,
			'Function with return type must return something.')
		verifyOp(this.opReturnType, SK.Val)
		const args = cat(this.opDeclareThis, this.args, this.opRestArg)
		withFun(this.kind, () => {
			verifyAndPlusLocals(args, () => {
				this.block.verify(this.isDo ? SK.Do : SK.Val)
			})
		})
		// name set by AssignSingle
	},

	FunAbstract() {
		verifyEach(this.args)
		verifyOp(this.opRestArg)
		verifyOp(this.opReturnType, SK.Val)
	},

	GetterFun(sk) {
		checkVal(this, sk)
		verifyName(this.name)
	},

	Ignore(sk) {
		checkDo(this, sk)
		for (const _ of this.ignoredNames)
			accessLocal(this, _)
	},

	Kind(sk) {
		checkVal(this, sk)
		verifyEach(this.superKinds, SK.Val)
		verifyOp(this.opDo, SK.Do)
		verifyEach(this.statics)
		verifyEach(this.methods)
		// name set by AssignSingle
	},

	Lazy(sk) {
		checkVal(this, sk)
		withBlockLocals(() => this.value.verify(SK.Val))
	},

	LocalAccess(sk) {
		checkVal(this, sk)
		const declare = locals.get(this.name)
		if (declare === undefined) {
			const builtinPath = options.builtinNameToPath.get(this.name)
			if (builtinPath === undefined)
				failMissingLocal(this.loc, this.name)
			else {
				const names = results.builtinPathToNames.get(builtinPath)
				if (names === undefined)
					results.builtinPathToNames.set(builtinPath, new Set([this.name]))
				else
					names.add(this.name)
			}
		} else {
			results.localAccessToDeclare.set(this, declare)
			setDeclareAccessed(declare, this)
		}
	},

	// Adding LocalDeclares to the available locals is done by Fun or lineNewLocals.
	LocalDeclare() {
		const builtinPath = options.builtinNameToPath.get(this.name)
		if (builtinPath !== undefined)
			warn(this.loc, `Local ${code(this.name)} overrides builtin from ${code(builtinPath)}.`)
		verifyOp(this.opType, SK.Val)
	},

	LocalMutate(sk) {
		checkDo(this, sk)
		this.value.verify(SK.Val)
	},

	Logic(sk) {
		checkVal(this, sk)
		check(this.args.length > 1, this.loc, 'Logic expression needs at least 2 arguments.')
		verifyEach(this.args, SK.Val)
	},

	Not(sk) {
		checkVal(this, sk)
		this.arg.verify(SK.Val)
	},

	NumberLiteral(sk) {
		checkVal(this, sk)
	},

	MapEntry(sk) {
		checkDo(this, sk)
		accessLocal(this, 'built')
		this.key.verify(SK.Val)
		this.val.verify(SK.Val)
	},

	Member(sk) {
		checkVal(this, sk)
		this.object.verify(SK.Val)
		verifyName(this.name)
	},

	MemberFun(sk) {
		checkVal(this, sk)
		verifyOp(this.opObject, SK.Val)
		verifyName(this.name)
	},

	MemberSet(sk) {
		checkDo(this, sk)
		this.object.verify(SK.Val)
		verifyName(this.name)
		verifyOp(this.opType, SK.Val)
		this.value.verify(SK.Val)
	},

	Method(sk) {
		checkVal(this, sk)
		makeUseOptional(this.fun.opDeclareThis)
		this.fun.args.forEach(makeUseOptional)
		opEach(this.fun.opRestArg, makeUseOptional)
		this.fun.verify(SK.Val)
		// name set by AssignSingle
	},

	MethodImpl() {
		verifyMethodImpl(this, () => {
			makeUseOptional(this.fun.opDeclareThis)
			this.fun.verify(SK.Val)
		})
	},
	MethodGetter() {
		verifyMethodImpl(this, () => {
			makeUseOptional(this.declareThis)
			verifyAndPlusLocals([this.declareThis], () => {
				this.block.verify(SK.Val)
			})
		})
	},
	MethodSetter() {
		verifyMethodImpl(this, () => {
			verifyAndPlusLocals([this.declareThis, this.declareFocus], () => {
				this.block.verify(SK.Do)
			})
		})
	},

	Module() {
		// No need to verify this.doImports.
		verifyEach(this.imports)
		withName(pathOptions.moduleName(), () => {
			verifyModuleLines(this.lines, this.loc)
		})
	},

	MsRegExp(sk) {
		checkVal(this, sk)
		this.parts.forEach(verifyName)
		// Check RegExp validity; only possible if this has a single part.
		if (this.parts.length === 1 && typeof this.parts[0] === 'string')
			try {
				/* eslint-disable no-new */
				new RegExp(this.parts[0])
			} catch (err) {
				if (!(err instanceof SyntaxError))
					// This should never happen.
					throw err
				fail(this.loc, err.message)
			}
	},

	New(sk) {
		checkVal(this, sk)
		this.type.verify(SK.Val)
		verifyEach(this.args, SK.val)
	},

	ObjEntryAssign(sk) {
		checkDo(this, sk)
		if (!results.isObjEntryExport(this))
			accessLocal(this, 'built')
		this.assign.verify(SK.Do)
		for (const _ of this.assign.allAssignees())
			setDeclareAccessed(_, this)
	},

	ObjEntryPlain(sk) {
		checkDo(this, sk)
		if (results.isObjEntryExport(this))
			check(typeof this.name === 'string', this.loc,
				'Module export must have a constant name.')
		else {
			accessLocal(this, 'built')
			verifyName(this.name)
		}
		this.value.verify(SK.Val)
	},

	ObjSimple(sk) {
		checkVal(this, sk)
		const keys = new Set()
		for (const {key, value, loc} of this.pairs) {
			check(!keys.has(key), loc, () => `Duplicate key ${key}`)
			keys.add(key)
			value.verify(SK.Val)
		}
	},

	Pipe(sk) {
		checkVal(this, sk)
		this.value.verify()
		for (const pipe of this.pipes)
			registerAndPlusLocal(LocalDeclare.focus(this.loc), () => {
				pipe.verify(SK.Val)
			})
	},

	QuotePlain(sk) {
		checkVal(this, sk)
		this.parts.forEach(verifyName)
	},

	QuoteSimple(sk) {
		checkVal(this, sk)
	},

	QuoteTaggedTemplate(sk) {
		checkVal(this, sk)
		this.tag.verify(SK.Val)
		this.quote.verify(SK.Val)
	},

	Range(sk) {
		checkVal(this, sk)
		this.start.verify(SK.Val)
		verifyOp(this.end, SK.Val)
	},

	SetSub(sk) {
		checkDo(this, sk)
		this.object.verify(SK.Val)
		verifyEach(this.subbeds, SK.Val)
		verifyOp(this.opType, SK.Val)
		this.value.verify(SK.Val)
	},

	SimpleFun(sk) {
		checkVal(this, sk)
		withBlockLocals(() => {
			withInFunKind(Funs.Plain, () => {
				registerAndPlusLocal(LocalDeclare.focus(this.loc), () => {
					this.value.verify()
				})
			})
		})
	},

	SpecialDo(sk) {
		checkDo(this, sk)
	},

	SpecialVal(sk) {
		checkVal(this, sk)
		setName(this)
	},

	Spread() {
		this.spreaded.verify(SK.Val)
	},

	SuperCall(sk) {
		check(method !== null, this.loc, 'Must be in a method.')
		results.superCallToMethod.set(this, method)

		if (method instanceof Constructor) {
			check(sk === SK.Do, this.loc, () =>
				`${showKeyword(Keywords.Super)} in constructor must appear as a statement.'`)
			results.constructorToSuper.set(method, this)
		}

		verifyEach(this.args, SK.Val)
	},

	SuperMember(sk) {
		checkVal(this, sk)
		check(method !== null, this.loc, 'Must be in method.')
		verifyName(this.name)
	},

	Switch(sk) {
		markStatement(this, sk)
		withIifeIfVal(sk, () => {
			withInSwitch(true, () => {
				this.switched.verify(SK.Val)
				verifyEach(this.parts, sk)
				verifyOp(this.opElse, sk)
			})
		})
	},

	SwitchPart(sk) {
		markStatement(this, sk)
		verifyEach(this.values, SK.Val)
		this.result.verify(sk)
	},

	Throw() {
		verifyOp(this.opThrown, SK.Val)
	},

	Import() {
		// Since Uses are always in the outermost scope, don't have to worry about shadowing.
		// So we mutate `locals` directly.
		function addUseLocal(_) {
			const prev = locals.get(_.name)
			check(prev === undefined, _.loc, () =>
				`${code(_.name)} already imported at ${prev.loc}`)
			verifyLocalDeclare(_)
			setLocal(_)
		}
		for (const _ of this.imported)
			addUseLocal(_)
		opEach(this.opImportDefault, addUseLocal)
	},

	With(sk) {
		markStatement(this, sk)
		this.value.verify(SK.Val)
		withIifeIfVal(sk, () => {
			if (sk === SK.Val)
				makeUseOptionalIfFocus(this.declare)
			verifyAndPlusLocal(this.declare, () => {
				this.block.verify(SK.Do)
			})
		})
	},

	Yield(_sk) {
		check(funKind === Funs.Generator, this.loc, () =>
			`Cannot ${showKeyword(Keywords.Yield)} outside of generator function.`)
		verifyOp(this.opValue, SK.Val)
	},

	YieldTo(_sk) {
		check(funKind === Funs.Generator, this.loc, () =>
			`Cannot ${showKeyword(Keywords.YieldTo)} outside of generator function.`)
		this.value.verify(SK.Val)
	}
})

// Helpers specific to certain MsAst types

function verifyFor(forLoop) {
	function verifyForBlock() {
		withLoop(forLoop, () => {
			forLoop.block.verify(SK.Do)
		})
	}
	ifElse(forLoop.opIteratee, _ => { withVerifyIteratee(_, verifyForBlock) }, verifyForBlock)
}

function withVerifyIteratee({element, bag}, action) {
	bag.verify(SK.Val)
	verifyNotLazy(element, 'Iteration element can not be lazy.')
	verifyAndPlusLocal(element, action)
}

function verifyMethodImpl(_, doVerify) {
	verifyName(_.symbol)
	withMethod(_, doVerify)
}

import {check} from '../context'
import {ObjEntry, LocalDeclare} from '../MsAst'
import {ifElse, isEmpty, last, rtail} from '../util'
import {Blocks, Modules} from '../VerifyResults'
import autoBlockKind, {opBlockBuildKind} from './autoBlockKind'
import {results} from './context'
import {plusLocals, verifyAndPlusLocal} from './locals'
import SK, {getSK} from './SK'
import verifyLines from './verifyLines'

export default function verifyBlock(sk) {
	if (sk === SK.Do)
		verifyDoBlock(this)
	else {
		check(!isEmpty(this.lines), 'Value block must have some content.')
		const kind = autoBlockKind(this.lines, this.loc)
		switch (kind) {
			case Blocks.Bag: case Blocks.Map: case Blocks.Obj:
				verifyBuiltLines(this.lines, this.loc)
				break
			case Blocks.Throw:
				verifyLines(this.lines)
				break
			case Blocks.Return:
				plusLocals(verifyLines(rtail(this.lines)), () => {
					last(this.lines).verify(SK.Val)
				})
				break
			default:
				throw new Error(kind)
		}
		results.blockToKind.set(this, kind)
	}
}

export function verifyDoBlock(_) {
	results.blockToKind.set(_, Blocks.Do)
	return verifyLines(_.lines)
}

export function verifyModuleLines(lines, loc) {
	results.moduleKind = ifElse(opBlockBuildKind(lines, loc),
		buildKind => {
			if (buildKind === Blocks.Obj) {
				for (const line of lines)
					if (line instanceof ObjEntry)
						results.objEntryExports.add(line)
				verifyLines(lines)
				return Modules.Exports
			} else {
				verifyBuiltLines(lines, loc)
				return buildKind === Blocks.Bag ? Modules.Bag : Modules.Map
			}
		},
		() => {
			if (isEmpty(lines))
				return Modules.Do
			else {
				const l = last(lines)
				const lastSK = getSK(l)
				if (lastSK === SK.Do) {
					verifyLines(lines)
					return Modules.Do
				} else {
					const newLocals = verifyLines(rtail(lines))
					plusLocals(newLocals, () => {
						l.verify(lastSK)
					})
					return Modules.Val
				}
			}
		})
}

function verifyBuiltLines(lines, loc) {
	verifyAndPlusLocal(LocalDeclare.built(loc), () => {
		verifyLines(lines)
	})
}

import {code} from '../../CompileError'
import {check} from '../context'
import {AssignDestructure, AssignSingle, ObjEntry} from '../MsAst'
import {assert, reverseIter} from '../util'
import {locals, pendingBlockLocals} from './context'
import {deleteLocal, registerLocal, setLocal} from './locals'
import SK from './SK'

/**
Verifies each line, accumulating locals.
@return List of every new local from `lines`.
*/
export default function verifyLines(lines) {
	/*
	We need to get all block locals up-front because
	Functions within lines can access locals from later lines.
	NOTE: We push these onto pendingBlockLocals in reverse
	so that when we iterate through lines forwards, we can pop from pendingBlockLocals
	to remove pending locals as they become real locals.
	It doesn't really matter what order we add locals in since it's not allowed
	to have two locals of the same name in the same block.
	*/
	const newLocals = []

	for (const line of reverseIter(lines))
		for (const _ of reverseIter(lineNewLocals(line))) {
			// Register the local now. Can't wait until the assign is verified.
			registerLocal(_)
			newLocals.push(_)
		}

	pendingBlockLocals.push(...newLocals)

	/*
	Keeps track of locals which have already been added in this block.
	Mason allows shadowing, but not within the same block.
	So, this is allowed:
		a = 1
		b =
			a = 2
			...
	But not:
		a = 1
		a = 2
	*/
	const thisBlockLocalNames = new Set()

	// All shadowed locals for this block.
	const shadowed = []

	for (const line of lines) {
		for (const newLocal of lineNewLocals(line)) {
			const name = newLocal.name
			const oldLocal = locals.get(name)
			if (oldLocal !== undefined) {
				check(!thisBlockLocalNames.has(name), newLocal.loc,
					() => `A local ${code(name)} is already in this block.`)
				shadowed.push(oldLocal)
			}
			thisBlockLocalNames.add(name)
			setLocal(newLocal)

			// Now that it's added as a local, it's no longer pending.
			// We added pendingBlockLocals in the right order that we can just pop them off.
			const popped = pendingBlockLocals.pop()
			assert(popped === newLocal)
		}
		line.verify(SK.Do)
	}

	newLocals.forEach(deleteLocal)
	shadowed.forEach(setLocal)
	return newLocals
}

function lineNewLocals(line) {
	return line instanceof AssignSingle ?
		[line.assignee] :
		line instanceof AssignDestructure ?
		line.assignees :
		line instanceof ObjEntry ?
		lineNewLocals(line.assign) :
		[]
}
